<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\User\Infrastructure\Driver\Rest;

use Psr\Http\Message\ResponseInterface;
use Psr\Http\Message\ServerRequestInterface;
use Throwable;
use OpenApi\Attributes as OA;
use DateTime;
use Civi\Lughauth\Features\Access\User\Domain\Gateway\UserFilter;
use Civi\Lughauth\Features\Access\User\Domain\Gateway\UserCursor;
use Civi\Lughauth\Features\Access\User\Domain\UserAttributes;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;
use Civi\Lughauth\Features\Access\User\Application\Usecase\List\UserListUsecase;
use Civi\Lughauth\Features\Access\Tenant\Domain\TenantRef;
use Civi\Lughauth\Shared\Security\AesCypherService;

class UserListController
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly UserListUsecase $listUsecase,
        private readonly AesCypherService $cypherService,
    ) {
    }
    #[OA\Get(
        path: "/api/access/users",
        tags: ["User"],
        description:"Create",
        responses: [
        new OA\Response(response: 201, description: "Ok", content: new OA\JsonContent(type: "object", ref:"#/components/schemas/UserApiListDTO")),
        new OA\Response(response: 422, description: "Constraint error"),
    ]
    )
    ]
    public function list(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("List User");
        $span = $this->startSpan("List User");
        try {
            $params = $request->getQueryParams();
            $filter = new UserFilter(
                uids: isset($params['uid']) ? [$params['uid']] : (isset($params['uids']) ? explode(',', $params['uids']) : null),
                search: $params['search'] ?? null,
                name: $params['name'] ?? null,
                tenant: isset($params['tenant']) ? new TenantRef($params['tenant']) : null,
                tenants: isset($params['tenants']) ? explode(",", $params['tenants']) : null,
            );
            $cursor = new UserCursor(
                limit: (int)($params['limit'] ?? 100),
                sinceUid: $params['since-uid'] ?? null,
                sinceName: $params['since-name'] ?? null,
                order: isset($params['order']) ? $this->extractOrder(explode(',', $params['order'])) : null,
            );
            $result = $this->listUsecase->list($filter, $cursor);
            $value = ['items' => array_map(fn ($item) => $this->mapUser($item), $result->values())];
            if ($nextLink = $this->nextLink($result->cursor(), $params)) {
                $value['next'] = "?{$nextLink}";
            }
            $response->getBody()->write(json_encode($value));
            return $response->withStatus(200)
              ->withHeader('Content-Type', 'application/json');
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function extractOrder(array $order): array
    {
        $this->logDebug("Extract order for User");
        $span = $this->startSpan("Extract order for User");
        try {
            $mapped = [];
            foreach ($order as $ord) {
                if ($ord === 'name-asc') {
                    $mapped[] = 'nameAsc';
                }
                if ($ord === 'name-desc') {
                    $mapped[] = 'nameDesc';
                }
            }
            return $mapped;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function nextLink(?UserCursor $value, array $params): ?string
    {
        $this->logDebug("Check generic allow for User");
        $span = $this->startSpan("Generic allow for User");
        try {
            if (!$value) {
                return null;
            }
            $link = [];
            if (isset($params['limit'])) {
                $link['limit'] = 'limit=' . $params['limit'];
            }
            if ($value && $value->sinceUid()) {
                $link['since-uid'] = 'since-uid=' . urlencode($value->sinceUid());
            }
            if ($value && strpos('NAME-ASC', $params['order'] ?? '') !== false && $value->sinceName()) {
                $link['since-name'] = 'since-name=' . urlencode($value->sinceName());
            }
            if ($value && strpos('NAME-DESC', $params['order'] ?? '') !== false && $value->sinceName()) {
                $link['since-name'] = 'since-name=' . urlencode($value->sinceName());
            }
            return implode('&', $link);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    private function mapUser(UserAttributes $value): UserApiDTO
    {
        $this->logDebug("Map entity to output dto for User");
        $span = $this->startSpan("Map entity to output dto for User");
        try {
            $tenant = $value->getTenant();
            $dto = new UserApiDTO();
            $dto->uid = $value->getUid();
            $dto->tenant = $tenant ? ['$ref' => $tenant->uid()] : null;
            $dto->name = $value->getName();
            $dto->password = '******';
            $dto->email = $value->getEmail();
            $dto->wellcomeAt = $value->getWellcomeAt()?->format(DateTime::ATOM);
            $dto->enabled = $value->isEnabled();
            $dto->approve = $value->getApprove();
            $dto->temporalPassword = $value->isTemporalPassword();
            $dto->useSecondFactors = $value->isUseSecondFactors();
            $dto->secondFactorSeed = '******';
            $dto->blockedUntil = $value->getBlockedUntil()?->format(DateTime::ATOM);
            $dto->provider = $value->getProvider();
            $dto->version = $value->getVersion();
            return $dto;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
