<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\User\Infrastructure\Driver\Rest;

use Psr\Http\Message\ResponseInterface;
use Psr\Http\Message\ServerRequestInterface;
use OpenApi\Attributes as OA;
use Throwable;
use DateTime;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;
use Civi\Lughauth\Features\Access\User\Application\Usecase\Retrieve\UserRetrieveUsecase;
use Civi\Lughauth\Shared\Security\AesCypherService;
use Civi\Lughauth\Features\Access\User\Application\Usecase\Retrieve\UserRetrieveResult;

class UserRetrieveController
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly UserRetrieveUsecase $retrieveUsecase,
        private readonly AesCypherService $cypherService,
    ) {
    }
    #[OA\Get(
        path: "/api/access/users{uid}",
        tags: ["User"],
        description:"Create",
        parameters: [
               new OA\PathParameter(name: "uid", required: true, schema: new OA\Schema(type: "string")),
          ],
        responses: [
              new OA\Response(response: 201, description: "Ok", content: new OA\JsonContent(type: "object", ref:"#/components/schemas/UserApiDTO")),
              new OA\Response(response: 404, description: "Not found"),
          ]
    )]
    public function retrieve(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("Retrieve User");
        $span = $this->startSpan("Retrieve User");
        try {
            if (!isset($args['uid'])) {
                throw new NotFoundException('-');
            }
            $uid = $args['uid'];
            $result = $this->retrieveUsecase->retrieve($uid);
            $value = $this->mapUser($result);
            $response->getBody()->write(json_encode($value));
            return $response->withStatus(200)
                  ->withHeader('Content-Type', 'application/json');
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    private function mapUser(UserRetrieveResult $value): UserApiDTO
    {
        $this->logDebug("Map entity to output dto for User");
        $span = $this->startSpan("Map entity to output dto for User");
        try {
            $tenant = $value->getTenant();
            $dto = new UserApiDTO();
            $dto->uid = $value->getUid();
            $dto->tenant = $tenant ? ['$ref' => $tenant->uid()] : null;
            $dto->name = $value->getName();
            $dto->password = '******';
            $dto->email = $value->getEmail();
            $dto->wellcomeAt = $value->getWellcomeAt()?->format(DateTime::ATOM);
            $dto->enabled = $value->isEnabled();
            $dto->approve = $value->getApprove();
            $dto->temporalPassword = $value->isTemporalPassword();
            $dto->useSecondFactors = $value->isUseSecondFactors();
            $dto->secondFactorSeed = '******';
            $dto->blockedUntil = $value->getBlockedUntil()?->format(DateTime::ATOM);
            $dto->provider = $value->getProvider();
            $dto->version = $value->getVersion();
            return $dto;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
