<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\UserAcceptedTermnsOfUse\Domain\Gateway;

use Civi\Lughauth\Features\Access\UserAcceptedTermnsOfUse\Domain\UserAcceptedTermnsOfUse;

class UserAcceptedTermnsOfUseCursor
{
    public function __construct(
        private int $limit,
        private ?string $sinceUid = null,
    ) {
    }
    public function next(UserAcceptedTermnsOfUse $userAcceptedTermnsOfUse, ?int $size): UserAcceptedTermnsOfUseCursor
    {
        $next = clone $this;
        $next->sinceUid = $userAcceptedTermnsOfUse->uid();
        $next->limit = $size ?? $this->limit;
        return $next;
    }
    public function limit(): int
    {
        return $this->limit;
    }
    public function sinceUid(): ?string
    {
        return $this->sinceUid;
    }
}
