use Civi\Lughauth\Features\Access\UserAcceptedTermnsOfUse\Domain\UserAcceptedTermnsOfUseRef;
use Civi\Lughauth\Features\Access\UserAcceptedTermnsOfUse\Domain\UserAcceptedTermnsOfUse;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class UserAcceptedTermnsOfUseReadRepositoryAdapter implements UserAcceptedTermnsOfUseReadRepository
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly UserAcceptedTermnsOfUsePdoConnector $conn,
    ) {
    }
    #[Override]
    public function resolve(UserAcceptedTermnsOfUseRef $ref): ?UserAcceptedTermnsOfUse
    {
        if ($ref instanceof UserAcceptedTermnsOfUse) {
            return $ref;
        } elseif (!$ref->_private_resolve) {
            $ref->_private_resolve = $this->conn->retrieve(new UserAcceptedTermnsOfUseFilter(uids: [ $ref->uid() ]));
        }
        return $ref->_private_resolve;
    }
    #[Override]
    public function list(?UserAcceptedTermnsOfUseFilter $filter = null, ?UserAcceptedTermnsOfUseCursor $sort = null): UserAcceptedTermnsOfUseSlide
    {
        $this->logDebug("List for User accepted termns of use on adapter ");
        $span = $this->startSpan("List for User accepted termns of use on adapter");
        try {
            $values = $this->conn->list($filter, $sort);
            $last = end($values);
            return new UserAcceptedTermnsOfUseSlide(function ($slide, $next) use ($filter) {
                return $this->list($filter, $next);
            }, new UserAcceptedTermnsOfUseCursor($sort?->limit() ?? 100, $last->uid ?? null), $values);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function retrieve(?UserAcceptedTermnsOfUseFilter $filter): ?UserAcceptedTermnsOfUse
    {
        $this->logDebug("Retrieve for User accepted termns of use on adapter ");
        $span = $this->startSpan("Retrieve for User accepted termns of use on adapter");
        try {
            return $this->conn->retrieve($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function exists(?UserAcceptedTermnsOfUseFilter $filter): bool
    {
        $this->logDebug("Exists for User accepted termns of use on adapter ");
        $span = $this->startSpan("Exists for User accepted termns of use on adapter");
        try {
            return $this->conn->exists($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function count(?UserAcceptedTermnsOfUseFilter $filter = null): int
    {
        $this->logDebug("Count for User accepted termns of use on adapter ");
        $span = $this->startSpan("Count for User accepted termns of use on adapter");
        try {
            return $this->conn->count($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function findOneByUid(string $uid): ?UserAcceptedTermnsOfUse
    {
        $this->logDebug("Find on by uid for User accepted termns of use on adapter");
        $span = $this->startSpan("Find on by uid for User accepted termns of use on adapter");
        try {
            return $this->conn->retrieve(new UserAcceptedTermnsOfUseFilter(uids: [$uid]));
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function findOneByUserAndConditions(UserRef $user, TenantTermsOfUseRef $conditions): ?UserAcceptedTermnsOfUse
    {
        $this->logDebug("Find on by user conditions for User accepted termns of use on adapter");
        $span = $this->startSpan("Find on by user conditions for User accepted termns of use on adapter");
        try {
            return $this->conn->retrieve(new UserAcceptedTermnsOfUseFilter(userAndConditions: UserAcceptedTermnsOfUseFilter::userAndConditionsFilter(user: $user, conditions: $conditions)));
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
