<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\UserAccessTemporalCode\Domain\ValueObject\Accesor;

use Civi\Lughauth\Features\Access\UserAccessTemporalCode\Domain\ValueObject\UserAccessTemporalCodeUserVO;
use Civi\Lughauth\Features\Access\User\Domain\UserRef;

trait UserAccessTemporalCodeUserAccesor
{
    private UserAccessTemporalCodeUserVO $_user;
    public function getUser(): UserRef
    {
        return $this->_user->value();
    }
    public function user(): UserAccessTemporalCodeUserVO
    {
        return $this->_user;
    }
    public function isUserChanged(?self $original = null): bool
    {
        return $this->_user->value() !== ($original ? $original->_user->value() : null);
    }
    public function withUser(UserAccessTemporalCodeUserVO|UserRef $user): self
    {
        $copy = clone $this;
        $copy->_user = UserAccessTemporalCodeUserVO::from($user);
        return $copy;
    }
}
