<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\UserIdentity\Application\Usecase\Delete;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Exception\UnauthorizedException;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Features\Access\UserIdentity\Domain\UserIdentityRef;
use Civi\Lughauth\Features\Access\UserIdentity\Domain\UserIdentity;
use Civi\Lughauth\Features\Access\UserIdentity\Application\Service\Visibility\UserIdentityVisibilityService;
use Civi\Lughauth\Features\Access\UserIdentity\Domain\Gateway\UserIdentityWriteGateway;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class UserIdentityDeleteUsecase
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly UserIdentityVisibilityService $visibility,
        private readonly UserIdentityWriteGateway $writer
    ) {
    }

    public function allowDelete(?UserIdentityRef $ref = null): Allow
    {
        $this->logDebug("Check allow of delete usecase for User identity");
        $span = $this->startSpan("Check allow of delete usecase for User identity");
        try {
            $result = $this->dispacher->dispatch(new UserIdentityDeleteAllowProposal(Allow::allowed('delete', 'Allowed to delete by default'), $ref));
            return $result->allow;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function delete(string $uid): void
    {
        $this->logDebug("Run delete usecase for User identity");
        $span = $this->startSpan("Run delete usecase for User identity");
        try {
            $ref = new UserIdentityRef($uid);
            $allow = $this->allowDelete($ref);
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            $input = $this->dispacher->dispatch(new UserIdentityDeleteInputProposal($ref));
            if (!$original = $this->visibility->retrieveVisibleForUpdate($input->ref)) {
                throw new NotFoundException($uid);
            }
            $deleted = $original->delete();
            $this->writer->delete($deleted);
            $this->dispacher->dispatch(new UserIdentityDeleteOuputProposal($ref));
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
