<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\UserIdentity\Application\Usecase\List;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Exception\UnauthorizedException;
use Civi\Lughauth\Features\Access\UserIdentity\Application\Service\Visibility\UserIdentityVisibilityService;
use Civi\Lughauth\Features\Access\UserIdentity\Domain\Gateway\UserIdentityReadGateway;
use Civi\Lughauth\Features\Access\UserIdentity\Domain\Gateway\UserIdentityFilter;
use Civi\Lughauth\Features\Access\UserIdentity\Domain\Gateway\UserIdentityCursor;
use Civi\Lughauth\Features\Access\UserIdentity\Domain\Gateway\UserIdentityAttributesSlide;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class UserIdentityListUsecase
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly UserIdentityVisibilityService $visibility,
        private readonly UserIdentityReadGateway $reader
    ) {
    }

    public function allowList(): Allow
    {
        $this->logDebug("Check allow of list usecase for User identity");
        $span = $this->startSpan("Check allow of list usecase for User identity");
        try {
            $result = $this->dispacher->dispatch(new UserIdentityListAllowDecision(Allow::allowed('list', 'Allowed to list by default')));
            return $result->allow;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function count(UserIdentityFilter $filter): int
    {
        $this->logDebug("Run list usecase for User identity");
        $span = $this->startSpan("Run list usecase for User identity");
        try {
            $allow = $this->allowList();
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            $input = $this->dispacher->dispatch(new UserIdentityListInputProposal($filter, null));
            return $this->visibility->countVisibles($input->filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function list(UserIdentityFilter $filter, ?UserIdentityCursor $cursor = null): UserIdentityAttributesSlide
    {
        $this->logDebug("Run list usecase for User identity");
        $span = $this->startSpan("Run list usecase for User identity");
        try {
            $allow = $this->allowList();
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            $input = $this->dispacher->dispatch(new UserIdentityListInputProposal($filter, $cursor));
            $slide = $this->visibility->listVisibles($input->filter, $input->cursor);
            $output = new UserIdentityAttributesSlide($slide->nextCursor(), array_map(fn ($item) => $this->visibility->copyWithHidden($item->toAttributes()), $slide->values()));
            return $this->dispacher->dispatch(new UserIdentityListOutputProposal($output))->slide;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
