<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\UserIdentity\Application\Usecase\Retrieve;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Exception\UnauthorizedException;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Features\Access\UserIdentity\Domain\UserIdentityAttributes;
use Civi\Lughauth\Features\Access\UserIdentity\Domain\UserIdentityRef;
use Civi\Lughauth\Features\Access\UserIdentity\Domain\UserIdentity;
use Civi\Lughauth\Features\Access\UserIdentity\Application\Service\Visibility\UserIdentityVisibilityService;
use Civi\Lughauth\Features\Access\UserIdentity\Domain\Gateway\UserIdentityReadGateway;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class UserIdentityRetrieveUsecase
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly UserIdentityVisibilityService $visibility,
        private readonly UserIdentityReadGateway $reader
    ) {
    }

    public function allowRetrieve(?UserIdentityRef $ref = null): Allow
    {
        $this->logDebug("Check allow of retrieve usecase for User identity");
        $span = $this->startSpan("Check allow of retrieve usecase for User identity");
        try {
            $result = $this->dispacher->dispatch(new UserIdentityRetrieveAllowDecision(Allow::allowed('retrieve', 'Allowed to retrieve by default'), $ref));
            return $result->allow;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function hiddenFields(?UserIdentityRef $ref = null): array
    {
        $this->logDebug("Check hidden fields usecase for User identity");
        $span = $this->startSpan("Check hidden fields usecase for User identity");
        try {
            return $this->visibility->fieldsToHide($ref);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function retrieve(string $uid): UserIdentityAttributes
    {
        $this->logDebug("Run retrieve usecase for User identity");
        $span = $this->startSpan("Run retrieve usecase for User identity");
        try {
            $ref = new UserIdentityRef($uid);
            $allow = $this->allowRetrieve($ref);
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            if (!$result = $this->visibility->retrieveVisible($ref)) {
                throw new NotFoundException($uid);
            }
            $input = $this->dispacher->dispatch(new UserIdentityRetrieveInputProposal($result))->ref;
            $output = $this->visibility->copyWithHidden($input->toAttributes());
            return $this->dispacher->dispatch(new UserIdentityRetrieveOutputProposal($output))->attributes;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
