<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\UserIdentity\Application\Usecase\Update;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Exception\UnauthorizedException;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Features\Access\UserIdentity\Domain\UserIdentityRef;
use Civi\Lughauth\Features\Access\UserIdentity\Domain\UserIdentityAttributes;
use Civi\Lughauth\Features\Access\UserIdentity\Application\Service\Visibility\UserIdentityVisibilityService;
use Civi\Lughauth\Features\Access\UserIdentity\Domain\Gateway\UserIdentityWriteGateway;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class UserIdentityUpdateUsecase
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly UserIdentityVisibilityService $visibility,
        private readonly UserIdentityWriteGateway $writer
    ) {
    }

    public function allowUpdate(?UserIdentityRef $ref = null): Allow
    {
        $this->logDebug("Check allow update usecase for User identity");
        $span = $this->startSpan("Check allow update usecase for User identity");
        try {
            $result = $this->dispacher->dispatch(new UserIdentityUpdateAllowProposal(Allow::allowed('update', 'Allowed to update by default'), $ref));
            return $result->allow;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function fixedFields(?UserIdentityRef $ref = null): array
    {
        $this->logDebug("Check fixed fields for update usecase for User identity");
        $span = $this->startSpan("Check fixed fields for update usecase for User identity");
        try {
            return $this->visibility->fieldsToFix($ref);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function update(string $uid, UserIdentityAttributes $attributes): UserIdentityAttributes
    {
        $this->logDebug("Run update usecase for User identity");
        $span = $this->startSpan("Run update usecase for User identity");
        try {
            $ref = new UserIdentityRef($uid);
            $allow = $this->allowUpdate($ref);
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            if (!$original = $this->visibility->retrieveVisibleForUpdate($ref)) {
                throw new NotFoundException($uid);
            }
            $input = $this->visibility->copyWithFixed($this->dispacher->dispatch(new UserIdentityUpdateInputProposal($ref, $attributes))->attributes);
            $modified = $original->replace($input);
            $result = $this->writer->update($original, $modified);
            $output = $this->visibility->copyWithHidden($result->toAttributes());
            return $this->dispacher->dispatch(new UserIdentityUpdateOutputProposal($output))->attributes;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
