<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\UserIdentity\Domain\Gateway;

use Psr\Container\ContainerInterface;
use Civi\Lughauth\Features\Access\UserIdentity\Domain\UserIdentity;
use Civi\Lughauth\Features\Access\UserIdentity\Infrastructure\Driven\UserIdentityReadRepositoryAdapter;
use Civi\Lughauth\Features\Access\UserIdentity\Domain\UserIdentityRef;

class UserIdentityReadGateway
{
    private UserIdentityReadRepository $repository;
    public function __construct(ContainerInterface $container, ?UserIdentityReadRepository $repository = null)
    {
        $this->repository = $repository ?? $container->get(UserIdentityReadRepositoryAdapter::class);
    }
    public function resolve(UserIdentityRef $ref): ?UserIdentity
    {
        return $this->repository->resolve($ref);
    }
    public function list(?UserIdentityFilter $filter = null, ?UserIdentityCursor $cursor = null): UserIdentitySlide
    {
        return $this->repository->list($filter, $cursor);
    }
    public function retrieve(UserIdentityFilter $filter): ?UserIdentity
    {
        return $this->repository->retrieve($filter);
    }
    public function exists(?UserIdentityFilter $filter): bool
    {
        return $this->repository->exists($filter);
    }
    public function count(?UserIdentityFilter $filter = null): int
    {
        return $this->repository->count($filter);
    }
    public function findOneByUid(string $uid): ?UserIdentity
    {
        return $this->repository->findOneByUid($uid);
    }
}
