<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\UserIdentity\Domain\ValueObject\Accesor;

use Civi\Lughauth\Features\Access\UserIdentity\Domain\ValueObject\UserIdentityRolesVO;
use Civi\Lughauth\Features\Access\UserIdentity\Domain\ValueObject\UserIdentityRolesListRef;

trait UserIdentityRolesAccesor
{
    private UserIdentityRolesVO $_roles;
    public function getRoles(): ?UserIdentityRolesListRef
    {
        return $this->_roles->value();
    }
    public function roles(): UserIdentityRolesVO
    {
        return $this->_roles;
    }
    public function isRolesChanged(?self $original = null): bool
    {
        return $this->_roles->value() !== ($original ? $original->_roles->value() : null);
    }
    public function withRoles(UserIdentityRolesVO|UserIdentityRolesListRef| array |null $roles): self
    {
        $copy = clone $this;
        $copy->_roles = UserIdentityRolesVO::from($roles);
        return $copy;
    }
}
