<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\UserIdentity\Domain\ValueObject\Accesor;

use Civi\Lughauth\Features\Access\UserIdentity\Domain\ValueObject\UserIdentityRolesRoleVO;
use Civi\Lughauth\Features\Access\Role\Domain\RoleRef;

trait UserIdentityRolesRoleAccesor
{
    private ?UserIdentityRolesRoleVO $_role = null;
    public function getRole(): ?RoleRef
    {
        return $this->_role?->value();
    }
    public function role(): ?UserIdentityRolesRoleVO
    {
        return $this->_role;
    }
    public function isRoleChanged(?self $original = null): bool
    {
        return $this->_role->value() !== ($original ? $original->_role->value() : null);
    }
    public function withRole(UserIdentityRolesRoleVO|RoleRef $role): self
    {
        $copy = clone $this;
        $copy->_role = UserIdentityRolesRoleVO::from($role);
        return $copy;
    }
}
