<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\UserIdentity\Domain\ValueObject\Holder;

use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Features\Access\UserIdentity\Domain\ValueObject\UserIdentityUserVO;
use Civi\Lughauth\Features\Access\User\Domain\UserRef;

trait UserIdentityUserAttributeHolder
{
    protected UserIdentityUserVO|UserRef|null $user = null;
    protected bool $userAssigned = false;

    public function getUserOrDefault(?UserIdentityUserVO $user): UserIdentityUserVO|UserRef|null
    {
        return $this->userAssigned ? ($this->user !== null ? UserIdentityUserVO::from($this->user) : null) : $user;
    }
    public function user(UserIdentityUserVO|UserRef|null $user): static
    {
        $this->user = $user;
        $this->userAssigned = true;
        return $this;
    }
    public function getUser(): ?UserRef
    {
        return is_a($this->user, UserIdentityUserVO::class) ? $this->user->value() : $this->user;
    }
    public function unsetUser(): static
    {
        $this->user = null;
        $this->userAssigned = false;
        return $this;
    }
    protected function withDefaultUser(): void
    {
    }
    protected function withAssertedUserRules(self $value, ConstraintFailList $errorsList): void
    {
        if ($value->userAssigned = $this->userAssigned) {
            $value->user = UserIdentityUserVO::tryFrom($this->user, $errorsList);
        }
    }
}
