<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\UserIdentity\Domain\ValueObject;

use InvalidArgumentException;

class UserIdentityRolesListRef implements \Iterator
{
    public static function fromArray(array $items): self
    {
        foreach ($items as $item) {
            if (!$item instanceof UserIdentityRolesItem) {
                throw new InvalidArgumentException("All items must be of type UserIdentityRolesItem");
            }
        }
        return new self(...$items);
    }

    private array $roles = [];

    public function __construct(UserIdentityRolesItem ...$roles)
    {
        $this->roles = $roles;
    }

    public function current(): UserIdentityRolesItem
    {
        return current($this->roles);
    }
    public function key(): int
    {
        return key($this->roles);
    }
    public function next(): void
    {
        next($this->roles);
    }
    public function rewind(): void
    {
        reset($this->roles);
    }
    public function valid(): bool
    {
        return key($this->roles) !== null;
    }
}
