<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\UserIdentity\Domain\ValueObject;

use Civi\Lughauth\Features\Access\Role\Domain\RoleRef;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFail;

class UserIdentityRolesRoleVO
{
    public static function from(UserIdentityRolesRoleVO|RoleRef $value): UserIdentityRolesRoleVO
    {
        if (is_a($value, UserIdentityRolesRoleVO::class)) {
            // If is a ValueObject, its already validated
            return $value;
        } else {
            // If is not a ValueObject, validation is need and exception throw
            $errorsList = new ConstraintFailList();
            return self::tryFrom($value, $errorsList);
        }
    }
    public static function tryFrom(mixed $value, ConstraintFailList $list): ?UserIdentityRolesRoleVO
    {
        if (is_a($value, UserIdentityRolesRoleVO::class)) {
            // If is a ValueObject, its already validated... nothing to append
            return $value;
        } elseif (is_a($value, RoleRef::class)) {
            return new UserIdentityRolesRoleVO($value);
        } else {
            $list->add(new ConstraintFail('wrong_type', ['role'], [$value], ['RoleRef']));
            return null;
        }
    }
    /**
     * private constructor to avoid build a value without all the rule validations.
     */
    private function __construct(
        private readonly RoleRef $role
    ) {
    }
    public function value(): RoleRef
    {
        return $this->role;
    }
}
