<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\UserIdentity\Domain\ValueObject;

use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFail;

class UserIdentityRolesVO
{
    public static function from(UserIdentityRolesVO|UserIdentityRolesListRef| array |null $value): UserIdentityRolesVO
    {
        if (is_a($value, UserIdentityRolesVO::class)) {
            // If is a ValueObject, its already validated
            return $value;
        } else {
            // If is not a ValueObject, validation is need and exception throw
            $errorsList = new ConstraintFailList();
            return self::tryFrom($value, $errorsList);
        }
    }
    public static function tryFrom(mixed $value, ConstraintFailList $list): ?UserIdentityRolesVO
    {
        if (is_a($value, UserIdentityRolesVO::class)) {
            // If is a ValueObject, its already validated... nothing to append
            return $value;
        } elseif (is_array($value)) {
            return new UserIdentityRolesVO(UserIdentityRolesListRef::fromArray($value));
        } elseif (!$value) {
            return new UserIdentityRolesVO($value);
        } elseif (is_a($value, UserIdentityRolesListRef::class)) {
            return new UserIdentityRolesVO($value);
        } else {
            $list->add(new ConstraintFail('wrong_type', ['roles'], [$value], ['UserIdentityRolesListRef']));
            return null;
        }
    }
    /**
     * private constructor to avoid build a value without all the rule validations.
     */
    private function __construct(
        private readonly ?UserIdentityRolesListRef $roles
    ) {
    }
    public function value(): ?UserIdentityRolesListRef
    {
        $value = $this->roles;
        if ($value) {
            $value->rewind();
        }
        return $value;
    }
    public function find(string $uid): ?UserIdentityRolesItem
    {
        foreach ($this->roles as $item) {
            if ($item->uid() == $uid) {
                return $item;
            }
        }
        return null;
    }
    public function getWithDefaults(UserIdentityRolesVO $defs): ?UserIdentityRolesVO
    {
        $values = [];
        $originals = $defs->value();
        foreach ($this->roles as $item) {
            $prev = null;
            foreach ($originals as $original) {
                if ($item->uid() == $original->uid()) {
                    $prev = $original;
                    break;
                }
            }
            $values[] = $prev ? $item->getOrDefault($prev) : $item;
        }
        return UserIdentityRolesVO::from(UserIdentityRolesListRef::fromArray($values));
    }
}
