<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\UserIdentity\Domain\ValueObject;

use Civi\Lughauth\Features\Access\TrustedClient\Domain\TrustedClientRef;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFail;

class UserIdentityTrustedClientVO
{
    public static function empty(): UserIdentityTrustedClientVO
    {
        return new UserIdentityTrustedClientVO(null);
    }
    public static function from(UserIdentityTrustedClientVO|TrustedClientRef|null $value): UserIdentityTrustedClientVO
    {
        if (is_a($value, UserIdentityTrustedClientVO::class)) {
            // If is a ValueObject, its already validated
            return $value;
        } else {
            // If is not a ValueObject, validation is need and exception throw
            $errorsList = new ConstraintFailList();
            $candidate = self::tryFrom($value, $errorsList);
            if ($errorsList->hasErrors()) {
                throw $errorsList->asConstraintException();
            }
            return $candidate;
        }
    }
    public static function tryFrom(mixed $value, ConstraintFailList $list): ?UserIdentityTrustedClientVO
    {
        if (is_a($value, UserIdentityTrustedClientVO::class)) {
            // If is a ValueObject, its already validated... nothing to append
            return $value;
        } elseif (!$value) {
            return new UserIdentityTrustedClientVO($value);
        } elseif (is_a($value, TrustedClientRef::class)) {
            return new UserIdentityTrustedClientVO($value);
        } else {
            $list->add(new ConstraintFail('wrong_type', ['trustedClient'], [$value], ['TrustedClientRef']));
            return null;
        }
    }
    /**
     * private constructor to avoid build a value without all the rule validations.
     */
    private function __construct(
        private readonly ?TrustedClientRef $trustedClient
    ) {
    }
    public function value(): ?TrustedClientRef
    {
        return $this->trustedClient;
    }
}
