    private function saveChilds(UserIdentity $parent): UserIdentity
    {
        $this->logDebug("Execute save childs sql query for User identity");
        $span = $this->startSpan("Execute save childs sql query for User identity");
        try {
            $roles = $parent->getRoles();
            if ($roles) {
                $uids = [];
                $saved = [];
                foreach ($roles as $child) {
                    if (!$this->existsRoles($parent->uid(), $child->uid())) {
                        $insertedChild = $this->insertRoles($parent->uid(), $child);
                        $saved[] = $insertedChild;
                        $uids[] = $insertedChild->uid();
                    } else {
                        $updatedChild = $this->updateRoles($parent->uid(), $child);
                        $saved[] = $updatedChild;
                        $uids[] = $updatedChild->uid();
                    }
                }
                $this->db->execute('DELETE FROM "access_user_identity_role" WHERE "user_identity" = :parent and  "uid" NOT IN (:uids)', [
                  new SqlParam(name: 'parent', value: $parent->uid(), type: SqlParam::STR),
                  new SqlParam(name: 'uids', value: $uids, type: SqlParam::STR),
                ]);
                $parent = $parent->withRoles(UserIdentityRolesListRef::fromArray($saved));
            }
            return $parent;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
