<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\UserIdentity\Infrastructure\Driven;

use Override;
use Throwable;
use Civi\Lughauth\Features\Access\UserIdentity\Infrastructure\Connector\Pdo\UserIdentityPdoConnector;
use Civi\Lughauth\Features\Access\UserIdentity\Domain\Gateway\UserIdentityReadRepository;
use Civi\Lughauth\Features\Access\UserIdentity\Domain\Gateway\UserIdentitySlide;
use Civi\Lughauth\Features\Access\UserIdentity\Domain\Gateway\UserIdentityFilter;
use Civi\Lughauth\Features\Access\UserIdentity\Domain\Gateway\UserIdentityCursor;
use Civi\Lughauth\Features\Access\UserIdentity\Domain\UserIdentityRef;
use Civi\Lughauth\Features\Access\UserIdentity\Domain\UserIdentity;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class UserIdentityReadRepositoryAdapter implements UserIdentityReadRepository
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly UserIdentityPdoConnector $conn,
    ) {
    }
    #[Override]
    public function resolve(UserIdentityRef $ref): ?UserIdentity
    {
        if ($ref instanceof UserIdentity) {
            return $ref;
        } elseif (!$ref->_private_resolve) {
            $ref->_private_resolve = $this->conn->retrieve(new UserIdentityFilter(uids: [ $ref->uid() ]));
        }
        return $ref->_private_resolve;
    }
    #[Override]
    public function list(?UserIdentityFilter $filter = null, ?UserIdentityCursor $sort = null): UserIdentitySlide
    {
        $this->logDebug("List for User identity on adapter ");
        $span = $this->startSpan("List for User identity on adapter");
        try {
            $values = $this->conn->list($filter, $sort);
            $last = end($values);
            return new UserIdentitySlide(function ($slide, $next) use ($filter) {
                return $this->list($filter, $next);
            }, new UserIdentityCursor($sort?->limit() ?? 100, $last->uid ?? null), $values);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function retrieve(?UserIdentityFilter $filter): ?UserIdentity
    {
        $this->logDebug("Retrieve for User identity on adapter ");
        $span = $this->startSpan("Retrieve for User identity on adapter");
        try {
            return $this->conn->retrieve($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function exists(?UserIdentityFilter $filter): bool
    {
        $this->logDebug("Exists for User identity on adapter ");
        $span = $this->startSpan("Exists for User identity on adapter");
        try {
            return $this->conn->exists($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function count(?UserIdentityFilter $filter = null): int
    {
        $this->logDebug("Count for User identity on adapter ");
        $span = $this->startSpan("Count for User identity on adapter");
        try {
            return $this->conn->count($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function findOneByUid(string $uid): ?UserIdentity
    {
        $this->logDebug("Find on by uid for User identity on adapter");
        $span = $this->startSpan("Find on by uid for User identity on adapter");
        try {
            return $this->conn->retrieve(new UserIdentityFilter(uids: [$uid]));
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
