<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\UserIdentity\Infrastructure\Driven;

use Psr\EventDispatcher\EventDispatcherInterface;
use Closure;
use Override;
use Throwable;
use Civi\Lughauth\Features\Access\UserIdentity\Domain\Gateway\UserIdentityWriteRepository;
use Civi\Lughauth\Features\Access\UserIdentity\Domain\Gateway\UserIdentitySlide;
use Civi\Lughauth\Features\Access\UserIdentity\Domain\Gateway\UserIdentityCursor;
use Civi\Lughauth\Features\Access\UserIdentity\Domain\Gateway\UserIdentityFilter;
use Civi\Lughauth\Features\Access\UserIdentity\Domain\UserIdentityRef;
use Civi\Lughauth\Features\Access\UserIdentity\Domain\UserIdentity;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class UserIdentityWriteRepositoryAdapter implements UserIdentityWriteRepository
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly UserIdentityPdoConnector $conn,
        private readonly EventDispatcherInterface $dispacher,
    ) {
    }
    #[Override]
    public function resolveForUpdate(UserIdentityRef $ref): ?UserIdentity
    {
        return $this->conn->retrieve(new UserIdentityFilter(uids: [ $ref->uid() ]));
    }
    #[Override]
    public function listForUpdate(?UserIdentityFilter $filter = null, ?UserIdentityCursor $sort = null): UserIdentitySlide
    {
        $this->logDebug("Count for User identity on adapter ");
        $span = $this->startSpan("Count for User identity on adapter");
        try {
            $values = $this->conn->list($filter, $sort);
            $last = end($values);
            return new UserIdentitySlide(function ($slide, $next) use ($filter) {
                return $this->listForUpdate($filter, $next);
            }, new UserIdentityCursor($sort?->limit ?? 100, $last->uid ?? null), $values);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function retrieveForUpdate(UserIdentityFilter $filter): ?UserIdentity
    {
        $this->logDebug("Count for User identity on adapter ");
        $span = $this->startSpan("Count for User identity on adapter");
        try {
            return $this->conn->retrieveForUpdate($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function existsForUpdate(?UserIdentityFilter $filter): bool
    {
        $this->logDebug("Count for User identity on adapter ");
        $span = $this->startSpan("Count for User identity on adapter");
        try {
            return $this->conn->exists($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function countForUpdate(?UserIdentityFilter $filter = null): int
    {
        $this->logDebug("Count for User identity on adapter ");
        $span = $this->startSpan("Count for User identity on adapter");
        try {
            return $this->conn->count($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function create(UserIdentity $entity, ?Closure $verify = null): UserIdentity
    {
        $this->logDebug("Count for User identity on adapter ");
        $span = $this->startSpan("Count for User identity on adapter");
        try {
            $created = $this->conn->create($entity, $verify);
            $this->dispach($entity);
            return $created;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function update(UserIdentityRef $reference, UserIdentity $entity): UserIdentity
    {
        $this->logDebug("Count for User identity on adapter ");
        $span = $this->startSpan("Count for User identity on adapter");
        try {
            $updated = $this->conn->update($entity);
            $this->dispach($entity);
            return $updated;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function delete(UserIdentity $entity): bool
    {
        $this->logDebug("Count for User identity on adapter ");
        $span = $this->startSpan("Count for User identity on adapter");
        try {
            $result = $this->conn->delete($entity);
            $this->dispach($entity);
            return $result;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function findOneForUpdateByUid(string $uid): ?UserIdentity
    {
        $this->logDebug("Find on by uid for User identity on adapter");
        $span = $this->startSpan("Find on by uid for User identity on adapter");
        try {
            return $this->conn->retrieveForUpdate(new UserIdentityFilter(uids: [$uid]));
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function dispach(UserIdentity $entity)
    {
        $this->logDebug("Count for User identity on adapter ");
        $span = $this->startSpan("Count for User identity on adapter");
        try {
            foreach ($entity->getTheEvents() as $event) {
                $this->dispacher->dispatch($event);
            }
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
