<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\UserIdentity\Infrastructure\Driver\Rest;

use Psr\Http\Message\ResponseInterface;
use Psr\Http\Message\ServerRequestInterface;
use Throwable;
use Civi\Lughauth\Features\Access\UserIdentity\Domain\Gateway\UserIdentityFilter;
use Civi\Lughauth\Features\Access\UserIdentity\Domain\Gateway\UserIdentityCursor;
use Civi\Lughauth\Features\Access\UserIdentity\Domain\UserIdentityAttributes;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;
use Civi\Lughauth\Features\Access\UserIdentity\Application\Usecase\List\UserIdentityListUsecase;
use Civi\Lughauth\Features\Access\User\Domain\UserRef;
use Civi\Lughauth\Features\Access\RelyingParty\Domain\RelyingPartyRef;
use Civi\Lughauth\Features\Access\TrustedClient\Domain\TrustedClientRef;

class UserIdentityListController
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly UserIdentityListUsecase $listUsecase,
        private readonly UserIdentityRestMapper $mapper,
    ) {
    }
    public function list(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("List User identity");
        $span = $this->startSpan("List User identity");
        try {
            $params = $request->getQueryParams();
            $filter = new UserIdentityFilter(
                uids: isset($params['uid']) ? [$params['uid']] : (isset($params['uids']) ? explode(',', $params['uids']) : null),
                search: $params['search'] ?? null,
                user: isset($params['user']) ? new UserRef($params['user']) : null,
                users: isset($params['users']) ? explode(",", $params['users']) : null,
                relyingParty: isset($params['relying-party']) ? new RelyingPartyRef($params['relying-party']) : null,
                relyingPartys: isset($params['relying-partys']) ? explode(",", $params['relying-partys']) : null,
                trustedClient: isset($params['trusted-client']) ? new TrustedClientRef($params['trusted-client']) : null,
                trustedClients: isset($params['trusted-clients']) ? explode(",", $params['trusted-clients']) : null,
            );
            $cursor = new UserIdentityCursor(
                limit: (int)($params['limit'] ?? 100),
                sinceUid: $params['since-uid'] ?? null,
            );
            $result = $this->listUsecase->list($filter, $cursor);
            $value = ['items' => array_map(fn ($item) => $this->mapper->mapUserIdentity($item), $result->values())];
            if ($nextLink = $this->nextLink($result->cursor(), $params)) {
                $value['next'] = "?{$nextLink}";
            }
            $response->getBody()->write(json_encode($value));
            return $response->withStatus(200)
              ->withHeader('Content-Type', 'application/json');
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function nextLink(?UserIdentityCursor $value, array $params): ?string
    {
        $this->logDebug("Check generic allow for User identity");
        $span = $this->startSpan("Generic allow for User identity");
        try {
            if (!$value) {
                return null;
            }
            $link = [];
            if (isset($params['limit'])) {
                $link['limit'] = 'limit=' . $params['limit'];
            }
            if ($value && $value->sinceUid()) {
                $link['since-uid'] = 'since-uid=' . urlencode($value->sinceUid());
            }
            return implode('&', $link);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
