<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\UserIdentity\Infrastructure\Driver\Rest;

use Psr\Http\Message\ResponseInterface;
use Psr\Http\Message\ServerRequestInterface;
use OpenApi\Attributes as OA;
use Throwable;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;
use Civi\Lughauth\Shared\Infrastructure\Sql\SqlTemplate;
use Civi\Lughauth\Features\Access\UserIdentity\Application\Usecase\Update\UserIdentityUpdateUsecase;

class UserIdentityUpdateController
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly SqlTemplate $sql,
        private readonly UserIdentityUpdateUsecase $updateUsecase,
        private readonly UserIdentityRestMapper $mapper,
    ) {
    }
    #[OA\Put(
        path: "/api/access/user-identities/{uid}",
        tags: ["User identity"],
        description: "Update the value",
        parameters: [
        new OA\PathParameter(name: "uid", required: true, schema: new OA\Schema(type: "string")),
    ],
        requestBody: new OA\RequestBody(content: new OA\JsonContent(type: "object", ref:"#/components/schemas/UserIdentityApiDTO")),
        responses: [
        new OA\Response(response: 200, description: "Ok", content: new OA\JsonContent(type: "object", ref:"#/components/schemas/UserIdentityApiDTO")),
        new OA\Response(response: 404, description: "Not found"),
        new OA\Response(response: 409, description: "Optimist lock"),
        new OA\Response(response: 422, description: "Constraint error"),
    ]
    )
    ]
    public function update(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("Update User identity");
        $span = $this->startSpan("Update User identity");
        $this->sql->begin();
        try {
            if (!isset($args['uid'])) {
                throw new NotFoundException('-');
            }
            $uid = $args['uid'];
            $value = $this->mapper->readUserIdentity($request);
            $result = $this->updateUsecase->update($uid, $value);
            $this->sql->commit();
            $value = $this->mapper->mapUserIdentity($result);
            $response->getBody()->write(json_encode($value));
            return $response->withStatus(200)
                  ->withHeader('Content-Type', 'application/json');
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $this->sql->close();
            $span->end();
        }
    }
}
