<?php

/* @autogenerated */
declare(strict_types=1);

namespace Civi\Lughauth\Features\Oidc\Authentication\Infrastructure\Driver\Html\Forms;

use Override;
use Psr\Http\Message\ResponseInterface;
use Psr\Http\Message\ServerRequestInterface;
use Civi\Lughauth\Features\Oidc\Authentication\Domain\AuthenticationRequest;
use Civi\Lughauth\Features\Oidc\Authentication\Domain\AuthenticationResult;
use Civi\Lughauth\Features\Oidc\Authentication\Domain\AuthorizedChalleges;
use Civi\Lughauth\Features\Oidc\User\Domain\PublicLoginAuthResponse;
use Civi\Lughauth\Features\Oidc\Authentication\Infrastructure\Driver\Html\Services\DecorateHtml;
use Civi\Lughauth\Features\Oidc\Authentication\Infrastructure\Driver\Html\Services\HtmlSecurer;
use Civi\Lughauth\Features\Oidc\Authentication\Infrastructure\Driver\Html\Services\PublicLogin;
use Civi\Lughauth\Features\Oidc\Authentication\Domain\Exception\LoginException;
use Civi\Lughauth\Shared\Infrastructure\Translation\MessageProvider;

class RecoverPassForm implements AuthorizationForm
{
    public function __construct(
        private readonly MessageProvider $messages,
        private readonly PublicLogin $publicLogin,
        private readonly DecorateHtml $decorator,
        private readonly HtmlSecurer $securer
    ) {
    }

    #[Override]
    public function step(): string
    {
        return 'recover-pass';
    }

    #[Override]
    public function handle(?AuthenticationResult $pe): bool
    {
        return $pe?->error == AuthenticationResult::ERR_WAITING_PASSCHANGE_CODE;
    }

    #[Override]
    public function paint(?AuthenticationResult $pe, string $locale, string $base, string $tenant, AuthorizedChalleges $challenges, ?array $body, ServerRequestInterface $request, ResponseInterface $response): ResponseInterface
    {
        $locale = '';
        $params = $request->getQueryParams();
        if (($params['recover_send'] ?? 'false') === 'true') {
            return $this->paintConfirm($pe, $locale, $base, $tenant, $challenges, $body, $request, $response);
        } elseif ($pe) {
            return $this->paintWait($pe, $locale, $base, $tenant, $challenges, $body, $request, $response);
        } else {
            return $this->paintAsk($pe, $locale, $base, $tenant, $challenges, $body, $request, $response);
        }
    }

    #[Override]
    public function autenticate(
        AuthenticationRequest $request,
        string $tenant,
        string $issuer,
        string $csid,
        string $state,
        string $nonce,
        AuthorizedChalleges $challenges,
        mixed $body
    ): PublicLoginAuthResponse {
        if (isset($body['user'])) {
            $user = $body['user'];
            $url = $this->publicLogin->askPassChange($request, $challenges, $user, $tenant, $issuer, $csid, $state, $nonce);
            throw new LoginException(AuthenticationResult::waitNewpass($url));
        } else {
            $code = $this->securer->decrypt($body["code"]);
            $newpass  = $this->securer->decrypt($body["new_pass"]);
            return $this->publicLogin->confirmPassChange($request, $challenges, $code, $newpass, $tenant, $issuer, $csid, $state, $nonce);
        }
    }

    public function paintAsk(?AuthenticationResult $pe, string $locale, string $base, string $tenant, AuthorizedChalleges $challenges, ?array $body, ServerRequestInterface $request, ResponseInterface $response): ResponseInterface
    {
        $js = $this->securer->configureScripts([
            $this->securer->addSign("sign"),
            $this->securer->focusOn("user"),
        ]);
        $translator = $this->messages->messages('forms', $locale, __DIR__ . '/../../Translations');

        $title = $translator->get("recoverpass.ask.title");
        $error = $pe?->errorMessage
            ? $translator->get("recoverpass.ask.error-format", [$error = $translator->get('error.' . strtolower($pe?->errorMessage))])
            : false;

        $help = $translator->get("recoverpass.ask.help");
        $send = $translator->get("recoverpass.ask.send");
        $name = $translator->get("recoverpass.ask.username");

        $backText = $translator->get(
            "recoverpass.ask.back-text",
            ["<input class=\"inline\" type=\"submit\" value=\"" . $translator->get("recoverpass.ask.back-label") . "\" />"]
        );
        $error = $error ? '<p class="error">' . $error . '</p>' : '';
        $response->getBody()->write($this->decorator->getFullPage(
            $request,
            'New pass',
            $js . <<<HTML
                    <h1> $title </h1>
                    <p> $help </p>
                    {$error}
                    <form method="POST">
                        <input type="hidden" name="csid" id="sign" />
                        <input type="hidden" name="step" value="recover-pass" />
                        <label>{$name}
                            <input type="text" name="user" id="user" value="" />
                        </label>
                        <input class="primary-button" type="submit" value="{$send}" />
                    </form>
                    <form method="POST">
                        <input type="hidden" name="step" value="" />
                        {$backText}
                    </form>
                HTML,
            $locale
        ));
        return $response;
    }

    public function paintConfirm(?AuthenticationResult $pe, string $locale, string $base, string $tenant, AuthorizedChalleges $challenges, ?array $body, ServerRequestInterface $request, ResponseInterface $response): ResponseInterface
    {
        $js = $this->securer->configureScripts([
            $this->securer->addSign("sign"),
            $this->securer->focusOn("type_code"),
            $this->securer->cypher([['from' => 'type_code', 'to' => 'code'],
                ['from' => 'type_new_pass', 'to' => 'new_pass']], 'recform'),
        ]);
        $translator = $this->messages->messages('forms', $locale, __DIR__ . '/../../Translations');

        $title = $translator->get("recoverpass.code.title");
        $error = $pe?->errorMessage
            ? $translator->get("recoverpass.code.error-format", [$error = $translator->get('error.' . strtolower($pe?->errorMessage))])
            : false;

        $help = $translator->get("recoverpass.code.help");
        $send = $translator->get("recoverpass.code.send");
        $code = $translator->get("recoverpass.code.validation");
        $pass = $translator->get("recoverpass.code.newpass");

        $backText = $translator->get(
            "recoverpass.code.back-text",
            ["<input class=\"inline\" type=\"submit\" value=\"" . $translator->get("recoverpass.code.back-label") . "\" />"]
        );
        $error = $error ? '<p class="error">' . $error . '</p>' : '';
        $response->getBody()->write($this->decorator->getFullPage(
            $request,
            'New pass',
            $js . <<<HTML
                    <h1> $title </h1>
                    <p> $help </p>
                    {$error}
                    <form id="recform" method="POST">
                        <input type="hidden" name="csid" id="sign" />
                        <input type="hidden" name="step" value="recover-pass" />
                        <input type="hidden" name="code" id="code" value="" />
                        <input type="hidden" name="new_pass" id="new_pass" value="" />
                        <label>{$code}
                            <input type="text" id="type_code" value="" />
                        </label>
                        <label>{$pass}
                            <input type="password" id="type_new_pass" id="new_pass" value="" />
                        </label>
                        <input class="primary-button" type="submit" value="{$send}" />
                    </form>
                    <form method="POST">
                        <input type="hidden" name="step" value="" />
                        {$backText}
                    </form>
                HTML
            ,
            $locale
        ));
        return $response;
    }

    public function paintWait(?AuthenticationResult $pe, string $locale, string $base, string $tenant, AuthorizedChalleges $challenges, ?array $body, ServerRequestInterface $request, ResponseInterface $response): ResponseInterface
    {
        $js = $this->securer->configureScripts([
            $this->securer->addSign("sign"),
            $this->securer->focusOn("type_code"),
            $this->securer->cypher([['from' => 'type_code', 'to' => 'code'],
                ['from' => 'type_new_pass', 'to' => 'new_pass']], 'recform'),
        ]);
        $translator = $this->messages->messages('forms', $locale, __DIR__ . '/../../Translations');

        $url = $pe->id;
        $title = $translator->get("recoverpass.info.title");
        $error = $pe?->errorMessage
            ? $translator->get("recoverpass.info.error-format", [$error = $translator->get('error.' . strtolower($pe?->errorMessage))])
            : false;

        $help = $translator->get("recoverpass.info.help");
        $send = $translator->get(
            "recoverpass.info.goto-text",
            ['<a href="'.$url.'">' . $translator->get("recoverpass.info.goto-label") . '</a>' ]
        );

        $backText = $translator->get(
            "recoverpass.info.back-text",
            ["<input class=\"inline\" type=\"submit\" value=\"" . $translator->get("recoverpass.info.back-label") . "\" />"]
        );
        $error = $error ? '<p class="error">' . $error . '</p>' : '';
        $response->getBody()->write($this->decorator->getFullPage(
            $request,
            'New pass',
            $js . <<<HTML
                    <h1>{$title}</h1>
                    <p>{$help}</p>
                    {$error}
                    {$send}
                    <form method="POST">
                        <input type="hidden" name="step" value="" />
                        {$backText}
                    </form>
                HTML,
            $locale
        ));
        return $response;
    }
}
