<?php

/* @autogenerated */
declare(strict_types=1);

namespace Civi\Lughauth\Features\Rbac\Acl\Domain;

class RuleSet
{
    public function __construct(
        private array $roles,
        private array $resources
    ) {
    }

    public static function fromArray(array $data): self
    {
        $roles = [];
        foreach ($data['roles'] ?? [] as $name => $r) {
            $roles[] = RoleRule::fromArray($name, $r);
        }

        $resources = [];
        foreach ($data['matches'] ?? [] as $name => $r) {
            $resources[] = ResourceRules::fromArray($name, $r);
        }

        return new self($roles, $resources);
    }

    public function getRole(string $name): ?RoleRule
    {
        foreach ($this->roles as $r) {
            if ($r->getName() === $name) {
                return $r;
            }
        }
        return null;
    }

    public function getMatchingResources(string $resourceName): array
    {
        $matches = [];

        foreach ($this->resources as $rules) {
            $pattern = $rules->getName();
            if ($pattern === $resourceName) {
                $matches[] = $rules;
            } elseif (str_starts_with($pattern, '/')) {
                // Regex match
                if (@preg_match($pattern, $resourceName)) {
                    $matches[] = $rules;
                }
            } elseif (str_contains($pattern, '*')) {
                // Wildcard match
                $regex = '/^' . str_replace('\*', '.*', preg_quote($pattern, '/')) . '$/';
                if (preg_match($regex, $resourceName)) {
                    $matches[] = $rules;
                }
            }
        }

        return $matches;
    }


    public function getResource(string $name): ?ResourceRules
    {
        foreach ($this->resources as $r) {
            if ($r->getName() === $name) {
                return $r;
            }
        }
        return null;
    }

    /** @return RoleRule[] */
    public function getRoles(): array
    {
        return $this->roles;
    }

    /** @return ResourceRules[] */
    public function getResources(): array
    {
        return $this->resources;
    }
}
