namespace Civi\Lughauth\Features\Rbac\Resource\Infrastructure\Driven;

use Override;
use Civi\Lughauth\Features\Rbac\Resource\Domain\Gateway\PartyVerifierRepository;
use Civi\Lughauth\Features\Access\RelyingParty\Domain\Gateway\RelyingPartyReadGateway;

class PartyVerifierAdapter implements PartyVerifierRepository
{
    public function __construct(private readonly RelyingPartyReadGateway $repository)
    {

    }
    /**
     * Devuelve el cliente si existe, o null si no estÃ¡ permitido.
     *
     * @param string $apiKey
     * @return string|null
     */
    #[Override]
    public function findOptional(string $apiKey): ?string
    {
        if (!$apiKey) {
            return null;
        }
        $party = $this->repository->findOneByApiKey($apiKey);
        return $party ? $party->getCode() : null;
    }
}
