<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Test\Plan\PlanTenant\Application\Service\Visibility;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Features\Test\Plan\PlanTenant\Domain\PlanTenantRef;
use Civi\Lughauth\Features\Test\Plan\PlanTenant\Domain\PlanTenantAttributes;
use Civi\Lughauth\Features\Test\Plan\PlanTenant\Domain\PlanTenant;
use Civi\Lughauth\Features\Test\Plan\PlanTenant\Domain\Gateway\PlanTenantFilter;
use Civi\Lughauth\Features\Test\Plan\PlanTenant\Domain\Gateway\PlanTenantCursor;
use Civi\Lughauth\Features\Test\Plan\PlanTenant\Domain\Gateway\PlanTenantReadGateway;
use Civi\Lughauth\Features\Test\Plan\PlanTenant\Domain\Gateway\PlanTenantSlide;
use Civi\Lughauth\Features\Test\Plan\PlanTenant\Domain\Gateway\PlanTenantWriteGateway;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class PlanTenantVisibilityService
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly PlanTenantReadGateway $readGateway,
        private readonly PlanTenantWriteGateway $writeGateway
    ) {
    }

    public function prepareVisibleDataInList(PlanTenant $content): PlanTenantAttributes
    {
        return $this->prepareVisibleDataCallback($content, true);
    }
    public function prepareVisibleData(PlanTenant $content): PlanTenantAttributes
    {
        return $this->prepareVisibleDataCallback($content, false);
    }
    public function copyWithFixed(PlanTenantAttributes $attributes, ?PlanTenant $original = null): PlanTenantAttributes
    {
        $this->logDebug("Copy with fixed for Plan tenant");
        $span = $this->startSpan("Copy with fixed for  Plan tenant");
        try {
            $visible = $this->visiblesReferences($attributes);
            $fixed = $this->fieldsToFix($original);
            foreach ($fixed as $field) {
                $visible->unset($field);
            }
            $result = $this->dispacher->dispatch(new PlanTenantPresetValues($attributes, $original));
            return $result->attributes;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    public function copyWithHidden(PlanTenantAttributes $attributes, ?PlanTenant $original = null): PlanTenantAttributes
    {
        $this->logDebug("Copy with hidden for Plan tenant");
        $span = $this->startSpan("Copy with hidden for  Plan tenant");
        try {
            $hidden = $this->fieldsToHide($original);
            foreach ($hidden as $field) {
                $attributes->unset($field);
            }
            return $attributes;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    public function checkVisibility(\Iterator|string|PlanTenantRef $value): bool
    {
        $this->logDebug("Check visibility of an iterator for Plan tenant");
        $span = $this->startSpan("Check visibility of an iterator for  Plan tenant");
        try {
            if (is_a($value, \Iterator::class)) {
                $ids = [];
                foreach ($value as $val) {
                    $ids[] = $val->uid();
                }
                $filter = new PlanTenantFilter(uids: $ids);
                return count($ids) == $this->countVisibles($filter);
            } elseif (is_a($value, PlanTenantRef::class)) {
                return !!$this->retrieveVisibleForUpdate($value);
            } else {
                return !!$this->retrieveVisibleForUpdate(new PlanTenantRef($value));
            }
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function listVisibles(PlanTenantFilter $filter, ?PlanTenantCursor $cursor = null): PlanTenantSlide
    {
        $this->logDebug("List visibles for Plan tenant");
        $span = $this->startSpan("List visibles for  Plan tenant");
        try {
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            $result = $this->readGateway->list($visibleFilter, $cursor);
            return $result->slide(function ($item) {
                return $this->evaluatePostVisibility($item);
            });
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function countVisibles(PlanTenantFilter $filter): int
    {
        $this->logDebug("Count visibles for Plan tenant");
        $span = $this->startSpan("Count visibles for  Plan tenant");
        try {
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            return $this->readGateway->count($visibleFilter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function listVisiblesForUpdate(PlanTenantFilter $filter, ?PlanTenantCursor $cursor = null): PlanTenantSlide
    {
        $this->logDebug("List visibles for update for Plan tenant");
        $span = $this->startSpan("List visibles for update for  Plan tenant");
        try {
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            $result = $this->writeGateway->listForUpdate($visibleFilter, $cursor);
            return $result->slide(function ($item) {
                return $this->evaluatePostVisibility($item);
            });
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function countVisiblesForUpdate(PlanTenantFilter $filter): int
    {
        $this->logDebug("Count visibles for update for Plan tenant");
        $span = $this->startSpan("Count visibles for update for  Plan tenant");
        try {
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            return $this->writeGateway->countForUpdate($visibleFilter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function retrieveVisible(PlanTenantRef $ref): ?PlanTenant
    {
        $this->logDebug("Retrieve visible for Plan tenant");
        $span = $this->startSpan("Retrieve visible for  Plan tenant");
        try {
            $filter = new PlanTenantFilter(uids: [$ref->uid()]);
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            if ($readed = $this->readGateway->retrieve($visibleFilter)) {
                return $this->evaluatePostVisibility($readed) ? $readed : null;
            } else {
                return null;
            }
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function retrieveVisibleForUpdate(PlanTenantRef $ref): ?PlanTenant
    {
        $this->logDebug("Retrieve visible for update for Plan tenant");
        $span = $this->startSpan("Retrieve visible for update for  Plan tenant");
        try {
            $filter = new PlanTenantFilter(uids: [$ref->uid()]);
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            if ($readed = $this->writeGateway->retrieveForUpdate($visibleFilter)) {
                return $this->evaluatePostVisibility($readed) ? $readed : null;
            } else {
                return null;
            }
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function fieldsToFix(?PlanTenantRef $ref = null): array
    {
        $this->logDebug("Check fields to fix for Plan tenant");
        $span = $this->startSpan("Check fields for fix for Plan tenant");
        try {
            $result = $this->dispacher->dispatch(new PlanTenantCollectNonEditableFields(PlanTenant::calculatedFields(), $ref));
            return array_values(array_unique(array_merge($this->fieldsToHide($ref), $result->fields)));
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    public function fieldsToHide(?PlanTenantRef $ref = null): array
    {
        $this->logDebug("Check fields to hide for Plan tenant");
        $span = $this->startSpan("Check fields to hide for  Plan tenant");
        try {
            $result = $this->dispacher->dispatch(new PlanTenantCollectNonVisibleFields([], $ref));
            return array_values(array_unique($result->fields));
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    private function visiblesReferences(PlanTenantAttributes $attributes): PlanTenantAttributes
    {
        $this->logDebug("Check visibility of parent references for Plan tenant");
        $span = $this->startSpan("Check visibility of parent references for  Plan tenant");
        try {
            return $attributes;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function applyPreVisibilityFilter(PlanTenantFilter $filter)
    {
        $this->logDebug("Compose visibility filter for Plan tenant");
        $span = $this->startSpan("Compose visibility filter for  Plan tenant");
        try {
            $result = $this->dispacher->dispatch(new PlanTenantRestrictFilterToVisibility($filter));
            return $result->planTenantFilter;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function evaluatePostVisibility(PlanTenant $value): bool
    {
        $this->logDebug("Check if item is visible for Plan tenant");
        $span = $this->startSpan("Check if item is visible for  Plan tenant");
        try {
            $result = $this->dispacher->dispatch(new PlanTenantVisibilityCheck(true, $value));
            return $result->visible;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function prepareVisibleDataCallback(PlanTenant $content, bool $inlist): PlanTenantAttributes
    {
        $this->logDebug("Prepare hidratation to visible data for Plan tenant");
        $span = $this->startSpan("Prepare hidratation to visible data for Plan tenant");
        try {
            $attributes = $content->toAttributes();
            $result = $this->dispacher->dispatch(new PlanTenantEnrichForView($content, $inlist, $attributes));
            return $result->getAttributes();
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
