<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Test\Plan\PlanTenant\Application\Usecase\List;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Exception\UnauthorizedException;
use Civi\Lughauth\Features\Test\Plan\PlanTenant\Application\Service\Visibility\PlanTenantVisibilityService;
use Civi\Lughauth\Features\Test\Plan\PlanTenant\Domain\Gateway\PlanTenantReadGateway;
use Civi\Lughauth\Features\Test\Plan\PlanTenant\Domain\Gateway\PlanTenantFilter;
use Civi\Lughauth\Features\Test\Plan\PlanTenant\Domain\Gateway\PlanTenantCursor;
use Civi\Lughauth\Features\Test\Plan\PlanTenant\Domain\Gateway\PlanTenantAttributesSlide;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class PlanTenantListUsecase
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly PlanTenantVisibilityService $visibility,
        private readonly PlanTenantReadGateway $reader
    ) {
    }

    public function allowList(): Allow
    {
        $this->logDebug("Check allow of list usecase for Plan tenant");
        $span = $this->startSpan("Check allow of list usecase for Plan tenant");
        try {
            $result = $this->dispacher->dispatch(new PlanTenantListAllowDecision(Allow::allowed('list', 'Allowed to list by default')));
            return $result->getAllow();
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function count(PlanTenantFilter $filter): int
    {
        $this->logDebug("Run list usecase for Plan tenant");
        $span = $this->startSpan("Run list usecase for Plan tenant");
        try {
            $allow = $this->allowList();
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            return $this->visibility->countVisibles($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function list(PlanTenantFilter $filter, ?PlanTenantCursor $cursor = null): PlanTenantAttributesSlide
    {
        $this->logDebug("Run list usecase for Plan tenant");
        $span = $this->startSpan("Run list usecase for Plan tenant");
        try {
            $allow = $this->allowList();
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            $slide = $this->visibility->listVisibles($filter, $cursor);
            return new PlanTenantAttributesSlide($slide->nextCursor(), array_map(fn ($item) => $this->visibility->copyWithHidden($this->visibility->prepareVisibleData($item)), $slide->values()));
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
