<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Test\Plan\PlanTenant\Application\Usecase\Retrieve;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Exception\UnauthorizedException;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Features\Test\Plan\PlanTenant\Domain\PlanTenantRef;
use Civi\Lughauth\Features\Test\Plan\PlanTenant\Application\Service\Visibility\PlanTenantVisibilityService;
use Civi\Lughauth\Features\Test\Plan\PlanTenant\Domain\Gateway\PlanTenantReadGateway;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class PlanTenantRetrieveUsecase
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly PlanTenantVisibilityService $visibility,
        private readonly PlanTenantReadGateway $reader
    ) {
    }

    public function allowRetrieve(?PlanTenantRef $ref = null): Allow
    {
        $this->logDebug("Check allow of retrieve usecase for Plan tenant");
        $span = $this->startSpan("Check allow of retrieve usecase for Plan tenant");
        try {
            $result = $this->dispacher->dispatch(new PlanTenantRetrieveAllowDecision(Allow::allowed('retrieve', 'Allowed to retrieve by default'), $ref));
            return $result->getAllow();
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function hiddenFields(?PlanTenantRef $ref = null): array
    {
        $this->logDebug("Check hidden fields usecase for Plan tenant");
        $span = $this->startSpan("Check hidden fields usecase for Plan tenant");
        try {
            return $this->visibility->fieldsToHide($ref);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function retrieve(string $uid): PlanTenantRetrieveResult
    {
        $this->logDebug("Run retrieve usecase for Plan tenant");
        $span = $this->startSpan("Run retrieve usecase for Plan tenant");
        try {
            $ref = new PlanTenantRef($uid);
            $allow = $this->allowRetrieve($ref);
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            if (!$result = $this->visibility->retrieveVisible($ref)) {
                throw new NotFoundException($uid);
            }
            $output = $this->visibility->copyWithHidden($this->visibility->prepareVisibleData($result));
            return new PlanTenantRetrieveResult($output);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
