<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Test\Plan\PlanTenant\Domain\Gateway;

use Civi\Lughauth\Features\Test\Plan\PlanTenant\Domain\PlanTenant;

class PlanTenantCursor
{
    public const NAME_ASC = 'name';
    public const NAME_DESC = '-name';

    public function __construct(
        private int $limit,
        private ?string $sinceUid = null,
        private ?string $sinceName = null,
        private ?array $order = null,
    ) {
    }
    public function next(PlanTenant $planTenant, ?int $size): PlanTenantCursor
    {
        $next = clone $this;
        $next->sinceUid = $planTenant->uid();
        $next->sinceName = $planTenant->getName();
        $next->limit = $size ?? $this->limit;
        return $next;
    }
    public function limit(): int
    {
        return $this->limit;
    }
    public function sinceUid(): ?string
    {
        return $this->sinceUid;
    }
    public function sinceName(): ?string
    {
        return $this->sinceName;
    }
    public function order(): ?array
    {
        return $this->order;
    }
}
