    {
        $this->repository = $repository ?? $container->get(PlanTenantReadRepositoryAdapter::class);
    }
    public function resolve(PlanTenantRef $ref): ?PlanTenant
    {
        return $this->repository->resolve($ref);
    }
    public function list(?PlanTenantFilter $filter = null, ?PlanTenantCursor $cursor = null): PlanTenantSlide
    {
        return $this->repository->list($filter, $cursor);
    }
    public function retrieve(PlanTenantFilter $filter): ?PlanTenant
    {
        return $this->repository->retrieve($filter);
    }
    public function exists(?PlanTenantFilter $filter): bool
    {
        return $this->repository->exists($filter);
    }
    public function count(?PlanTenantFilter $filter = null): int
    {
        return $this->repository->count($filter);
    }
}
