namespace Civi\Lughauth\Features\Test\Plan\PlanTenant\Domain\Gateway;

use Psr\Container\ContainerInterface;
use Closure;
use Civi\Lughauth\Features\Test\Plan\PlanTenant\Domain\PlanTenant;
use Civi\Lughauth\Features\Test\Plan\PlanTenant\Domain\PlanTenantRef;
use Civi\Lughauth\Features\Test\Plan\PlanTenant\Infrastructure\Driven\PlanTenantWriteRepositoryAdapter;

class PlanTenantWriteGateway
{
    private PlanTenantWriteRepository $repository;
    public function __construct(ContainerInterface $container, ?PlanTenantWriteRepository $repository = null)
    {
        $this->repository = $repository ?? $container->get(PlanTenantWriteRepositoryAdapter::class);
    }
    public function create(PlanTenant $entity, ?Closure $verify = null): PlanTenant
    {
        return $this->repository->create($entity, $verify);
    }
    public function update(PlanTenantRef $ref, PlanTenant $entity): PlanTenant
    {
        return $this->repository->update($ref, $entity);
    }
    public function delete(PlanTenant $entity): bool
    {
        return $this->repository->delete($entity);
    }
    public function listForUpdate(?PlanTenantFilter $filter = null, ?PlanTenantCursor $cursor = null): PlanTenantSlide
    {
        return $this->repository->listForUpdate($filter, $cursor);
    }
    public function retrieveForUpdate(PlanTenantFilter $filter): ?PlanTenant
    {
        return $this->repository->retrieveForUpdate($filter);
    }
    public function existsForUpdate(?PlanTenantFilter $filter): bool
    {
        return $this->repository->existsForUpdate($filter);
    }
    public function countForUpdate(?PlanTenantFilter $filter = null): int
    {
        return $this->repository->countForUpdate($filter);
    }
}
