<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Test\Plan\PlanTenant\Domain;

use Civi\Lughauth\Features\Test\Plan\PlanTenant\Domain\ValueObject\Holder\PlanTenantUidAttributeHolder;
use Civi\Lughauth\Features\Test\Plan\PlanTenant\Domain\ValueObject\PlanTenantUidVO;
use Civi\Lughauth\Features\Test\Plan\PlanTenant\Domain\ValueObject\Holder\PlanTenantNameAttributeHolder;
use Civi\Lughauth\Features\Test\Plan\PlanTenant\Domain\ValueObject\PlanTenantNameVO;
use Civi\Lughauth\Features\Test\Plan\PlanTenant\Domain\ValueObject\Holder\PlanTenantVersionAttributeHolder;
use Civi\Lughauth\Features\Test\Plan\PlanTenant\Domain\ValueObject\PlanTenantVersionVO;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;

class PlanTenantAttributes
{
    use PlanTenantUidAttributeHolder;
    use PlanTenantNameAttributeHolder;
    use PlanTenantVersionAttributeHolder;

    private const UNSETS = [
      'uid' => 'unsetUid',
      'name' => 'unsetName',
      'version' => 'unsetVersion',
    ];

    public function build(): PlanTenant
    {
        $errors = new ConstraintFailList();
        $uid = PlanTenantUidVO::tryFrom($this->uid, $errors);
        $name = PlanTenantNameVO::tryFrom($this->name, $errors);
        $version = PlanTenantVersionVO::tryFrom($this->version, $errors);
        if ($errors->hasErrors()) {
            throw $errors->asConstraintException();
        }    return new PlanTenant(
            uid: $uid,
            name: $name,
            version: $version,
        );
    }
    public function withAssertedRules(): PlanTenantAttributes
    {
        $value = new PlanTenantAttributes();
        $errorsList = new ConstraintFailList();
        $this->withAssertedUidRules($value, $errorsList);
        $this->withAssertedNameRules($value, $errorsList);
        $this->withAssertedVersionRules($value, $errorsList);
        if ($errorsList->hasErrors()) {
            throw $errorsList->asConstraintException();
        }
        return $value;
    }
    public function unset($field)
    {
        if (isset(self::UNSETS[$field])) {
            call_user_func([$this, self::UNSETS[$field]]);
        }
    }
    public function withDefaults(): PlanTenantAttributes
    {
        $this->withDefaultUid();
        $this->withDefaultName();
        $this->withDefaultVersion();
        return $this;
    }
}
