<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Test\Plan\PlanTenant\Infrastructure\Driven;

use Override;
use Throwable;
use Civi\Lughauth\Features\Test\Plan\PlanTenant\Infrastructure\Connector\Pdo\PlanTenantPdoConnector;
use Civi\Lughauth\Features\Test\Plan\PlanTenant\Domain\Gateway\PlanTenantReadRepository;
use Civi\Lughauth\Features\Test\Plan\PlanTenant\Domain\Gateway\PlanTenantSlide;
use Civi\Lughauth\Features\Test\Plan\PlanTenant\Domain\Gateway\PlanTenantFilter;
use Civi\Lughauth\Features\Test\Plan\PlanTenant\Domain\Gateway\PlanTenantCursor;
use Civi\Lughauth\Features\Test\Plan\PlanTenant\Domain\PlanTenantRef;
use Civi\Lughauth\Features\Test\Plan\PlanTenant\Domain\PlanTenant;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class PlanTenantReadRepositoryAdapter implements PlanTenantReadRepository
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly PlanTenantPdoConnector $conn,
    ) {
    }
    #[Override]
    public function resolve(PlanTenantRef $ref): ?PlanTenant
    {
        if ($ref instanceof PlanTenant) {
            return $ref;
        } elseif (!$ref->_private_resolve) {
            $ref->_private_resolve = $this->conn->retrieve(new PlanTenantFilter(uids: [ $ref->uid() ]));
        }
        return $ref->_private_resolve;
    }
    #[Override]
    public function list(?PlanTenantFilter $filter = null, ?PlanTenantCursor $sort = null): PlanTenantSlide
    {
        $this->logDebug("List for Plan tenant on adapter ");
        $span = $this->startSpan("List for Plan tenant on adapter");
        try {
            $values = $this->conn->list($filter, $sort);
            $last = end($values);
            return new PlanTenantSlide(function ($slide, $next) use ($filter) {
                return $this->list($filter, $next);
            }, new PlanTenantCursor($sort?->limit ?? 100, $last->uid ?? null), $values);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function retrieve(?PlanTenantFilter $filter): ?PlanTenant
    {
        $this->logDebug("Retrieve for Plan tenant on adapter ");
        $span = $this->startSpan("Retrieve for Plan tenant on adapter");
        try {
            return $this->conn->retrieve($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function exists(?PlanTenantFilter $filter): bool
    {
        $this->logDebug("Exists for Plan tenant on adapter ");
        $span = $this->startSpan("Exists for Plan tenant on adapter");
        try {
            return $this->conn->exists($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function count(?PlanTenantFilter $filter = null): int
    {
        $this->logDebug("Count for Plan tenant on adapter ");
        $span = $this->startSpan("Count for Plan tenant on adapter");
        try {
            return $this->conn->count($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
