<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Test\Plan\PlanTenant\Infrastructure\Driven;

use Psr\EventDispatcher\EventDispatcherInterface;
use Closure;
use Override;
use Throwable;
use Civi\Lughauth\Features\Test\Plan\PlanTenant\Domain\Gateway\PlanTenantWriteRepository;
use Civi\Lughauth\Features\Test\Plan\PlanTenant\Infrastructure\Connector\Pdo\PlanTenantPdoConnector;
use Civi\Lughauth\Features\Test\Plan\PlanTenant\Domain\Gateway\PlanTenantSlide;
use Civi\Lughauth\Features\Test\Plan\PlanTenant\Domain\Gateway\PlanTenantCursor;
use Civi\Lughauth\Features\Test\Plan\PlanTenant\Domain\Gateway\PlanTenantFilter;
use Civi\Lughauth\Features\Test\Plan\PlanTenant\Domain\PlanTenantRef;
use Civi\Lughauth\Features\Test\Plan\PlanTenant\Domain\PlanTenant;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;
use Civi\Lughauth\Shared\Infrastructure\EntityChangelog\EntityChangelogService;

class PlanTenantWriteRepositoryAdapter implements PlanTenantWriteRepository
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly PlanTenantPdoConnector $conn,
        private readonly EventDispatcherInterface $dispacher,
        private readonly EntityChangelogService $changelog,
    ) {
    }
    #[Override]
    public function resolveForUpdate(PlanTenantRef $ref): ?PlanTenant
    {
        return $this->conn->retrieveForUpdate(new PlanTenantFilter(uids: [ $ref->uid() ]));
    }
    #[Override]
    public function listForUpdate(?PlanTenantFilter $filter = null, ?PlanTenantCursor $sort = null): PlanTenantSlide
    {
        $this->logDebug("Count for Plan tenant on adapter ");
        $span = $this->startSpan("Count for Plan tenant on adapter");
        try {
            $values = $this->conn->listForUpdate($filter, $sort);
            $last = end($values);
            return new PlanTenantSlide(function ($slide, $next) use ($filter) {
                return $this->listForUpdate($filter, $next);
            }, new PlanTenantCursor($sort?->limit ?? 100, $last->uid ?? null), $values);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function retrieveForUpdate(PlanTenantFilter $filter): ?PlanTenant
    {
        $this->logDebug("Count for Plan tenant on adapter ");
        $span = $this->startSpan("Count for Plan tenant on adapter");
        try {
            return $this->conn->retrieveForUpdate($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function existsForUpdate(?PlanTenantFilter $filter): bool
    {
        $this->logDebug("Count for Plan tenant on adapter ");
        $span = $this->startSpan("Count for Plan tenant on adapter");
        try {
            return $this->conn->existsForUpdate($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function countForUpdate(?PlanTenantFilter $filter = null): int
    {
        $this->logDebug("Count for Plan tenant on adapter ");
        $span = $this->startSpan("Count for Plan tenant on adapter");
        try {
            return $this->conn->countForUpdate($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function create(PlanTenant $entity, ?Closure $verify = null): PlanTenant
    {
        $this->logDebug("Count for Plan tenant on adapter ");
        $span = $this->startSpan("Count for Plan tenant on adapter");
        try {
            $created = $this->conn->create($entity, $verify);
            $this->dispach($entity);
            $this->changelog->recordChange('plan-tenant', $entity->uid(), $entity->asPublicJson(), []);
            return $created;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function update(PlanTenantRef $reference, PlanTenant $entity): PlanTenant
    {
        $this->logDebug("Count for Plan tenant on adapter ");
        $span = $this->startSpan("Count for Plan tenant on adapter");
        try {
            $updated = $this->conn->update($entity);
            $this->dispach($entity);
            $original = ($reference instanceof PlanTenant) ? $reference : $this->conn->retrieve(new PlanTenantFilter(uids: [ $reference->uid() ]));
            $this->changelog->recordChange('plan-tenant', $entity->uid(), $entity->asPublicJson(), $original->asPublicJson());
            return $updated;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function delete(PlanTenant $entity): bool
    {
        $this->logDebug("Count for Plan tenant on adapter ");
        $span = $this->startSpan("Count for Plan tenant on adapter");
        try {
            $result = $this->conn->delete($entity);
            $this->dispach($entity);
            $this->changelog->recordDeletion('plan-tenant', $entity->uid(), $entity->asPublicJson());
            return $result;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function dispach(PlanTenant $entity)
    {
        $this->logDebug("Count for Plan tenant on adapter ");
        $span = $this->startSpan("Count for Plan tenant on adapter");
        try {
            foreach ($entity->getTheEvents() as $event) {
                $this->dispacher->dispatch($event);
            }
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
