<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Test\Plan\PlanTenant\Infrastructure\Driver\Rest;

use Psr\Http\Message\ResponseInterface;
use Psr\Http\Message\ServerRequestInterface;
use OpenApi\Attributes as OA;
use Throwable;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;
use Civi\Lughauth\Features\Test\Plan\PlanTenant\Application\Usecase\Retrieve\PlanTenantRetrieveUsecase;
use Civi\Lughauth\Features\Test\Plan\PlanTenant\Application\Usecase\Retrieve\PlanTenantRetrieveResult;

class PlanTenantRetrieveController
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly PlanTenantRetrieveUsecase $retrieveUsecase,
    ) {
    }
    #[OA\Get(
        path: "/api/plan/tenants{uid}",
        tags: ["Plan tenant"],
        description:"Create",
        parameters: [
               new OA\PathParameter(name: "uid", required: true, schema: new OA\Schema(type: "string")),
          ],
        responses: [
              new OA\Response(response: 201, description: "Ok", content: new OA\JsonContent(type: "object", ref:"#/components/schemas/PlanTenantApiDTO")),
              new OA\Response(response: 404, description: "Not found"),
          ]
    )]
    public function retrieve(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("Retrieve Plan tenant");
        $span = $this->startSpan("Retrieve Plan tenant");
        try {
            if (!isset($args['uid'])) {
                throw new NotFoundException('-');
            }
            $uid = $args['uid'];
            $result = $this->retrieveUsecase->retrieve($uid);
            $value = $this->mapPlanTenant($result);
            $response->getBody()->write(json_encode($value));
            return $response->withStatus(200)
                  ->withHeader('Content-Type', 'application/json');
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    private function mapPlanTenant(PlanTenantRetrieveResult $value): PlanTenantApiDTO
    {
        $this->logDebug("Map entity to output dto for Plan tenant");
        $span = $this->startSpan("Map entity to output dto for Plan tenant");
        try {
            $dto = new PlanTenantApiDTO();
            $dto->uid = $value->getUid();
            $dto->name = $value->getName();
            $dto->version = $value->getVersion();
            return $dto;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
