<?php

/* @autogenerated */
declare(strict_types=1);

namespace Civi\Lughauth\Shared\Exception;

/**
 * Exception thrown when an operation fails due to the presence of non-empty child elements.
 *
 * Typically used to indicate that an entity or resource cannot be deleted or modified
 * because it still contains dependent or nested children.
 */
class NotEmptyChildsException extends \RuntimeException
{
    /**
     * Constructs a new NotEmptyChildsException using the provided inner exception.
     */
    public function __construct(\Exception $ex)
    {
        $code = $ex->getCode();
        parent::__construct($ex->getMessage(), is_int($code) ? $code : 0, $ex);
    }
}
