<?php

/* @autogenerated */
declare(strict_types=1);

namespace Civi\Lughauth\Shared\Infrastructure\Middelware;

use Slim\App;
use Slim\Psr7\Response;
use Psr\Http\Message\ServerRequestInterface;
use Psr\Http\Message\ResponseInterface;
use Psr\Http\Server\RequestHandlerInterface;
use Civi\Lughauth\Shared\AppConfig;

/**
 * Adds CORS headers to responses based on configuration.
 *
 * @api
 */
class CorsMiddleware
{
    /**
     * Registers the middleware and the OPTIONS handler on the Slim app.
     *
     * @param App $app Slim application instance.
     */
    public static function register(App $app)
    {
        $app->add(CorsMiddleware::class);
        $app->options('/{routes:.+}', function ($request, $response, $args) {
            return $response;
        });
    }

    /**
     * Creates a new CORS middleware instance.
     */
    public function __construct(
        /** @var AppConfig Configuration provider. */
        private readonly AppConfig $config
    ) {
    }

    /**
     * Adds CORS headers to the response and handles preflight requests.
     */
    public function __invoke(ServerRequestInterface $request, RequestHandlerInterface $handler): ResponseInterface
    {
        if ($request->getMethod() === 'OPTIONS') {
            $response = $this->addHeaders(new Response(), $request);
            return $response->withStatus(204);
        } else {
            return $this->addHeaders($handler->handle($request), $request);
        }
    }

    /**
     * Applies the configured CORS headers to a response.
     *
     * @param ResponseInterface $response Response to modify.
     * @param ServerRequestInterface $request Incoming request.
     */
    private function addHeaders(ResponseInterface $response, ServerRequestInterface $request): ResponseInterface
    {
        $enabled = $this->config->is('app.http.cors.enabled');
        if ($enabled) {
            $origin = $this->config->get('app.http.cors.origin', $request->hasHeader('Origin') ? $request->getHeader('Origin') : '*');
            return $response
                ->withHeader('Access-Control-Allow-Origin', $origin)
                ->withHeader('Access-Control-Allow-Methods', $this->config->get('app.http.cors.allow.methods', 'GET, POST, PUT, PATCH, DELETE, OPTIONS'))
                ->withHeader('Access-Control-Allow-Headers', $this->config->get('app.http.cors.allow.headers', 'Content-Type, Authorization, Accept-Language, traceparent, x-api-key'))
                ->withHeader('Access-Control-Allow-Credentials', $this->config->get('app.http.cors.allow.credentials', 'true'))
                ->withHeader('Access-Control-Expose-Headers', $this->config->get('app.http.cors.expose.headers', 'Content-Disposition'))
                ->withHeader('Access-Control-Max-Age', $this->config->get('app.http.cors.max-age.headers', '86400'));
        } else {
            return $response;
        }
    }
}
