<?php

/* @autogenerated */
declare(strict_types=1);

namespace Civi\Lughauth\Shared\Security;

/**
 * Represents the identity of an authenticated or anonymous user, including
 * roles, claims, and authorization scope.
 */
class Identity
{
    /**
     * No authorization scope granted.
     */
    public const AUTH_SCOPE_NONE = 'none';
    /**
     * Read-only authorization scope granted.
     */
    public const AUTH_SCOPE_READ = 'read';
    /**
     * Read and write authorization scope granted.
     */
    public const AUTH_SCOPE_BOTH = 'read-write';

    /**
     * Creates an anonymous identity.
     *
     * @return Identity An instance representing an anonymous user.
     */
    public static function anonimous(): Identity
    {
        return new Identity(anonimous: true);
    }

    /**
     * Constructs an Identity object.
     */
    public function __construct(
        /** @var bool Whether the user is anonymous. */
        public readonly bool $anonimous,
        /** @var string|null The authorization scope (none, read, or read-write). */
        public readonly ?string $authScope = self::AUTH_SCOPE_NONE,
        /** @var string|null The user identifier. */
        public readonly ?string $id = null,
        /** @var string|null The user display name. */
        public readonly ?string $name = null,
        /** @var string|null The access token used for authentication. */
        public readonly ?string $token = null,
        /** @var string|null The issuer of the identity (e.g., authentication server). */
        public readonly ?string $issuer = null,
        /** @var string|null The tenant associated with the user, if any. */
        public readonly ?string $tenant = null,
        /** @var array<int, string>|null The list of roles granted to the user. */
        public readonly ?array $roles = null,
        /** @var array<int, string>|null The groups to which the user belongs. */
        public readonly ?array $groups = null,
        /** @var array<string, string>|null Additional claims or metadata associated with the user. */
        public readonly ?array $claims = null,
        /** @var string|null A raw scope string (space-separated). */
        public readonly ?string $scope = null,
        /** @var string|null The user email address. */
        public readonly ?string $email = null,
    ) {
    }

    /**
     * Retrieves the value of a specific claim.
     *
     * @param string $claim The name of the claim.
     * @return string|null The value of the claim if present, or null otherwise.
     */
    public function getClaim(string $claim): ?string
    {
        return $this->claims && isset($this->claims[$claim]) ? $this->claims[$claim] : null;
    }

    /**
     * Checks if the identity has a specific scope.
     *
     * @param string $scope The scope to check.
     * @return bool Always returns false (not yet implemented).
     */
    public function hasScope(string $scope): bool
    {
        return $this->scope !== null && in_array($scope, explode(' ', $this->scope), true);
    }

    /**
     * Checks if the identity has at least one of the given roles.
     *
     * @param string ...$roles One or more roles to check.
     * @return bool True if any of the roles match; false otherwise.
     */
    public function hasAnyRole(string ...$roles): bool
    {
        if (!$this->roles) {
            return false;
        }
        foreach ($roles as $role) {
            if ($this->hasRole($role)) {
                return true;
            }
        }
        return false;
    }

    /**
     * Checks if the identity has a specific role.
     *
     * @param string $role The role to check.
     * @return bool True if the role is present; false otherwise.
     */
    public function hasRole(string $role): bool
    {
        return $this->roles ? false !== array_search($role, $this->roles) : false;
    }
}
