<?php

/* @autogenerated */
declare(strict_types=1);

namespace Civi\Lughauth\Shared\Value\Validation\Rule;

use Override;
use Civi\Lughauth\Shared\Value\Validation\Rule;
use Civi\Lughauth\Shared\Value\Validation\RuleFail;
use Respect\Validation\Validator;

/**
 * Length class validates that the given value has a length between a minimum and maximum value.
 * This can be used for strings, arrays, and other countable data structures.
 * @api
 */
class Length implements Rule
{
    /**
     * Create a new rule with length must be beetween min and max
     *
     * @param int $min The minimum length.
     * @param int $max The maximum length.
     */
    public function __construct(
        private readonly ?int $min = null,
        private readonly ?int $max = null
    ) {
    }

    /**
     * Checks whether the given value has a length between the specified min and max values.
     *
     * @param mixed $value The value to validate.
     * @return RuleFail|null Returns null if validation passes, otherwise a RuleFail object.
     */
    #[Override]
    public function check($value): ?RuleFail
    {
        return Validator::length($this->min, $this->max)->isValid($value) ? null : new RuleFail('rule_length', $value, ['min' => $this->min, 'max' => $this->max]);
    }
}
