<?php

/* @autogenerated */
declare(strict_types=1);

namespace Civi\Lughauth\Shared\Value\Validation\Rule;

use Override;
use Civi\Lughauth\Shared\Value\Validation\Rule;
use Civi\Lughauth\Shared\Value\Validation\RuleFail;
use Respect\Validation\Validator;

/**
 * MacAddress class validates that the given value is a valid MAC address (Media Access Control address).
 * It supports both colon (:) and hyphen (-) as separators.
 * @api
 */
class MacAddress implements Rule
{
    /**
     * Checks whether the given value is a valid MAC address.
     *
     * @param mixed $value The value to validate.
     * @return RuleFail|null Returns null if validation passes, otherwise a RuleFail object.
     */
    #[Override]
    public function check($value): ?RuleFail
    {
        /** @psalm-suppress UndefinedInterfaceMethod */
        return Validator::macAddress()->isValid($value) ? null : new RuleFail('rule_mac_addres', $value, []);
    }
}
