<?php

/* @autogenerated */
declare(strict_types=1);

namespace Civi\Lughauth\Shared\Value\Validation\Rule;

use Override;
use Civi\Lughauth\Shared\Value\Validation\Rule;
use Civi\Lughauth\Shared\Value\Validation\RuleFail;
use Respect\Validation\Validator;

/**
 * Min class validates that the given value is greater than or equal to a specified minimum value.
 * @api
 */
class Min implements Rule
{
    /**
     * Create a rule witch value must be al least the parameter
     *
     * @param mixed $min The minimum value.
     */
    public function __construct(
        private readonly int $min
    ) {
    }
    /**
     * Checks whether the given value is greater than or equal to the minimum value.
     *
     * @param mixed $value The value to validate.
     * @return RuleFail|null Returns null if validation passes, otherwise a RuleFail object.
     */
    #[Override]
    public function check($value): ?RuleFail
    {
        return Validator::min($this->min)->isValid($value) ? null : new RuleFail('rule_min', $value, ['min' => $this->min]);
    }
}
