<?php

/* @autogenerated */
declare(strict_types=1);

namespace Civi\Lughauth\Shared\Value\Validation\Rule;

use Override;
use Civi\Lughauth\Shared\Value\Validation\Rule;
use Civi\Lughauth\Shared\Value\Validation\RuleFail;
use Respect\Validation\Validator;

/**
 * PostalCode class validates that the given value is a valid postal code for a specified country.
 * @api
 */
class PostalCode implements Rule
{
    /**
     * Validate the postal code on reference to a country.
     */
    public function __construct(
        /** @var string The country code for postal code validation. */
        private readonly string $countryCode
    ) {
    }
    /**
     * Checks whether the given value is a valid postal code for the specified country.
     *
     * @param mixed $value The value to validate.
     * @return RuleFail|null Returns null if validation passes, otherwise a RuleFail object.
     */
    #[Override]
    public function check($value): ?RuleFail
    {
        return Validator::postalCode($this->countryCode)->isValid($value) ? null : new RuleFail('rule_postal_code', $value, ['countryCode' => $this->countryCode]);
    }
}
