<?php

/* @autogenerated */
declare(strict_types=1);

namespace Civi\Lughauth\Shared\Value\Validation\Rule;

use Override;
use Civi\Lughauth\Shared\Value\Validation\Rule;
use Civi\Lughauth\Shared\Value\Validation\RuleFail;
use Respect\Validation\Validator;

/**
 * Regex class validates that the given value matches a specified regular expression pattern.
 * @api
 */
class Regex implements Rule
{
    /**
     * Validate against the provided pattern.
     */
    public function __construct(
        /** @var string The regular expression pattern. */
        private readonly string $pattern
    ) {
    }

    /**
     * Checks whether the given value matches the specified regular expression pattern.
     *
     * @param mixed $value The value to validate.
     * @return RuleFail|null Returns null if validation passes, otherwise a RuleFail object.
     */
    #[Override]
    public function check($value): ?RuleFail
    {
        /** @psalm-suppress UndefinedInterfaceMethod */
        return Validator::regex($this->pattern)->isValid($value) ? null : new RuleFail('rule_regexp', $value, ['pattern' => $this->pattern]);
    }
}
