<?php

/* @autogenerated */
declare(strict_types=1);

namespace Civi\Lughauth\Shared\Value\Validation\Rule\Regional;

use Override;
use Civi\Lughauth\Shared\Value\Validation\Rule;
use Civi\Lughauth\Shared\Value\Validation\RuleFail;
use Respect\Validation\Validator;

/**
 * Nip class validates that the given value is a valid Polish taxpayer identification number (NIP).
 * @api
 */
class Nip implements Rule
{
    /**
     * Checks whether the given value is a valid Polish NIP.
     *
     * @param mixed $value The value to validate.
     * @return RuleFail|null Returns null if validation passes, otherwise a RuleFail object.
     */
    #[Override]
    public function check($value): ?RuleFail
    {
        return Validator::nip()->isValid($value) ? null : new RuleFail('rule_nip', $value, []);
    }
}
