<?php

/* @autogenerated */
declare(strict_types=1);

namespace Civi\Lughauth\Shared\Value\Validation\Rule;

use Override;
use Civi\Lughauth\Shared\Value\Validation\Rule;
use Civi\Lughauth\Shared\Value\Validation\RuleFail;
use Respect\Validation\Validator;

/**
 * SubdivisionCode class validates that the given value is a valid ISO 3166-2 subdivision code (e.g., US-CA for California).
 * @api
 */
class SubdivisionCode implements Rule
{
    /**
     * Validate agains the country subdivision codes.
     *
     * @param string $countryCode The country code for subdivision validation.
     */
    public function __construct(
        private readonly string $countryCode
    ) {
    }

    /**
     * Checks whether the given value is a valid ISO 3166-2 subdivision code for the specified country.
     *
     * @param mixed $value The value to validate.
     * @return RuleFail|null Returns null if validation passes, otherwise a RuleFail object.
     */
    #[Override]
    public function check($value): ?RuleFail
    {
        return Validator::subdivisionCode($this->countryCode)->isValid($value) ? null : new RuleFail('rule_subdivision_code', $value, ['countryCode' => $this->countryCode]);
    }
}
