<?php

# @autogenerated
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Features\Access\ApiKeyClient\Domain\ApiKeyClient;

final class ApiKeyClientUnitTest extends TestCase
{
    public function test_init_store_values(): void
    {
        // @Arrange
        $one = new ApiKeyClient(
            uid: 'one',
            code: 'one',
            key: 'one',
            enabled: true,
            scopes: 'one',
            version: 1,
        );

        // @Act
        $other = $one->toAttributes()->build();
        $calculated = ApiKeyClient::calculatedFields();

        // @Assert
        $this->assertEquals([ 'enabled'], $calculated);
        $this->assertEquals($one->uid(), $other->uid());
        $this->assertEquals($one->getCode(), $other->getCode());
        $this->assertTrue($one->isCodeChanged());
        $this->assertEquals($one->getKey(), $other->getKey());
        $this->assertTrue($one->isKeyChanged());
        $this->assertEquals($one->getScopes(), $other->getScopes());
        $this->assertTrue($one->isScopesChanged());
        $this->assertEquals($one->getVersion(), $other->getVersion());
        $this->assertTrue($one->isVersionChanged());
    }
    public function test_replace_change_values(): void
    {
        // @Arrange
        $base = new ApiKeyClient(
            uid: 'one',
            code: 'one',
            key: 'one',
            enabled: true,
            scopes: 'one',
            version: 1,
        );
        $other = new ApiKeyClient(
            uid: 'other',
            code: 'other',
            key: 'other',
            enabled: false,
            scopes: 'other',
            version: 2,
        );

        // @Act
        $one = $base->replace($other->toAttributes());

        // @Assert
        $this->assertEquals($one->uid(), $base->uid());
        $this->assertEquals($one->getCode(), $other->getCode());
        $this->assertTrue($one->isCodeChanged($base));
        $this->assertEquals($one->getKey(), $other->getKey());
        $this->assertTrue($one->isKeyChanged($base));
        $this->assertEquals($one->getScopes(), $other->getScopes());
        $this->assertTrue($one->isScopesChanged($base));
        $this->assertEquals($one->getVersion(), $other->getVersion());
        $this->assertTrue($one->isVersionChanged($base));
    }
    public function test_json_compare(): void
    {
        // @Arrange
        $one = new ApiKeyClient(
            uid: 'one',
            code: 'one',
            key: 'one',
            enabled: true,
            scopes: 'one',
            version: 1,
        );

        // @Act
        $json = $one->asPublicJson();

        // @Assert
        $this->assertEquals('one', $json['uid']);
        $this->assertEquals('one', $json['code']);
        $this->assertEquals('one', $json['key']);
        $this->assertEquals(true, $json['enabled']);
        $this->assertEquals('one', $json['scopes']);
        $this->assertEquals(1, $json['version']);
    }
    public function test_create_store_values(): void
    {
        // @Arrange
        $one = new ApiKeyClient(
            uid: 'one',
            code: 'one',
            key: 'one',
            enabled: true,
            scopes: 'one',
            version: 1,
        );

        // @Act
        $other = ApiKeyClient::create($one->toAttributes());

        // @Assert
        $this->assertEquals($one->uid(), $other->uid());
        $this->assertEquals($one->getCode(), $other->getCode());
        $this->assertTrue($one->isCodeChanged());
        $this->assertEquals($one->getKey(), $other->getKey());
        $this->assertTrue($one->isKeyChanged());
        $this->assertEquals($one->getScopes(), $other->getScopes());
        $this->assertTrue($one->isScopesChanged());
        $this->assertEquals($one->getVersion(), $other->getVersion());
        $this->assertTrue($one->isVersionChanged());
        $this->assertCount(0, $one->getTheEvents());
        $this->assertCount(1, $other->getTheEvents());
    }
    public function test_update_change_values(): void
    {
        $base = new ApiKeyClient(
            uid: 'one',
            code: 'one',
            key: 'one',
            enabled: true,
            scopes: 'one',
            version: 1,
        );
        $other = new ApiKeyClient(
            uid: 'other',
            code: 'other',
            key: 'other',
            enabled: false,
            scopes: 'other',
            version: 2,
        );

        // @Act
        $one = $base->update($other->toAttributes());

        // @Assert
        $this->assertEquals($one->uid(), $base->uid());
        $this->assertEquals($one->getCode(), $other->getCode());
        $this->assertTrue($one->isCodeChanged($base));
        $this->assertEquals($one->getKey(), $other->getKey());
        $this->assertTrue($one->isKeyChanged($base));
        $this->assertEquals($one->getScopes(), $other->getScopes());
        $this->assertTrue($one->isScopesChanged($base));
        $this->assertEquals($one->getVersion(), $other->getVersion());
        $this->assertTrue($one->isVersionChanged($base));
        $this->assertCount(0, $other->getTheEvents());
        $this->assertCount(1, $one->getTheEvents());
    }
    public function test_delete_store_values(): void
    {
        // @Arrange
        $one = new ApiKeyClient(
            uid: 'one',
            code: 'one',
            key: 'one',
            enabled: true,
            scopes: 'one',
            version: 1,
        );

        // @Act
        $other = $one->delete();

        // @Assert
        $this->assertEquals($one->uid(), $other->uid());
        $this->assertEquals($one->getCode(), $other->getCode());
        $this->assertTrue($one->isCodeChanged());
        $this->assertEquals($one->getKey(), $other->getKey());
        $this->assertTrue($one->isKeyChanged());
        $this->assertEquals($one->getScopes(), $other->getScopes());
        $this->assertTrue($one->isScopesChanged());
        $this->assertEquals($one->getVersion(), $other->getVersion());
        $this->assertTrue($one->isVersionChanged());
    }
    public function test_enable_modify(): void
    {
        // @Arrange
        $source = new ApiKeyClient(
            uid: 'one',
            code: 'one',
            key: 'one',
            enabled: true,
            scopes: 'one',
            version: 1,
        );
        $sourceEnabled = false;
        $targetEnabled = true;
        $source = $source->withEnabled($sourceEnabled);

        // @Act
        $target = $source->enable();

        // @Assert
        $this->assertEquals(false, $source->isEnabled());
        $this->assertEquals($targetEnabled, $target->isEnabled());
        $this->assertNotEquals($sourceEnabled, $target->isEnabled());
    }
    public function test_disable_modify(): void
    {
        // @Arrange
        $source = new ApiKeyClient(
            uid: 'one',
            code: 'one',
            key: 'one',
            enabled: true,
            scopes: 'one',
            version: 1,
        );
        $sourceEnabled = true;
        $targetEnabled = false;
        $source = $source->withEnabled($sourceEnabled);

        // @Act
        $target = $source->disable();

        // @Assert
        $this->assertEquals(true, $source->isEnabled());
        $this->assertEquals($targetEnabled, $target->isEnabled());
        $this->assertNotEquals($sourceEnabled, $target->isEnabled());
    }
}
