<?php

# @autogenerated
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Features\Access\Master\CustomRole\Domain\ValueObject\CustomRoleTenantVO;
use Civi\Lughauth\Features\Access\Tenant\Domain\TenantRef;

final class CustomRoleTenantVOUnitTest extends TestCase
{
    public function test_asignation_keep_value(): void
    {
        $value = new TenantRef('one');
        $ref = CustomRoleTenantVO::from($value);
        $this->assertEquals(new TenantRef('one'), $ref->value());
        $other = CustomRoleTenantVO::tryFrom($ref, new ConstraintFailList());
        $this->assertSame($other, $ref);
        $more = CustomRoleTenantVO::from($ref);
        $this->assertSame($more, $ref);
    }
    public function test_asignation_invalid_type(): void
    {
        $errors = new ConstraintFailList();
        $other = CustomRoleTenantVO::tryFrom('1', $errors);
        $this->assertNull($other);
        $this->assertTrue($errors->hasErrors());
    }
    public function test_empty(): void
    {
        $ref = CustomRoleTenantVO::empty();
        $this->assertNull($ref->value());
    }
    public function test_try_from_empty(): void
    {
        $ref = CustomRoleTenantVO::tryFrom(null, new ConstraintFailList());
        $this->assertNull($ref->value());
    }
}
