<?php

# @autogenerated
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Features\Access\Master\Role\Domain\ValueObject\RoleTenantVO;
use Civi\Lughauth\Features\Access\Master\Tenant\Domain\TenantRef;

final class RoleTenantVOUnitTest extends TestCase
{
    public function test_asignation_keep_value(): void
    {
        $value = new TenantRef('one');
        $ref = RoleTenantVO::from($value);
        $this->assertEquals(new TenantRef('one'), $ref->value());
        $other = RoleTenantVO::tryFrom($ref, new ConstraintFailList());
        $this->assertSame($other, $ref);
        $more = RoleTenantVO::from($ref);
        $this->assertSame($more, $ref);
    }
    public function test_asignation_invalid_type(): void
    {
        $errors = new ConstraintFailList();
        $other = RoleTenantVO::tryFrom('1', $errors);
        $this->assertNull($other);
        $this->assertTrue($errors->hasErrors());
    }
    public function test_empty(): void
    {
        $ref = RoleTenantVO::empty();
        $this->assertNull($ref->value());
    }
    public function test_try_from_empty(): void
    {
        $ref = RoleTenantVO::tryFrom(null, new ConstraintFailList());
        $this->assertNull($ref->value());
    }
}
