<?php

# @autogenerated
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Features\Access\Master\TenantPartySecurity\Domain\ValueObject\TenantPartySecurityRelyingPartyVO;
use Civi\Lughauth\Features\Access\Master\RelyingParty\Domain\RelyingPartyRef;

final class TenantPartySecurityRelyingPartyVOUnitTest extends TestCase
{
    public function test_asignation_keep_value(): void
    {
        $value = new RelyingPartyRef('one');
        $ref = TenantPartySecurityRelyingPartyVO::from($value);
        $this->assertEquals(new RelyingPartyRef('one'), $ref->value());
        $other = TenantPartySecurityRelyingPartyVO::tryFrom($ref, new ConstraintFailList());
        $this->assertSame($other, $ref);
        $more = TenantPartySecurityRelyingPartyVO::from($ref);
        $this->assertSame($more, $ref);
    }
    public function test_asignation_invalid_type(): void
    {
        $errors = new ConstraintFailList();
        $other = TenantPartySecurityRelyingPartyVO::tryFrom('1', $errors);
        $this->assertNull($other);
        $this->assertTrue($errors->hasErrors());
    }
}
