<?php

# @autogenerated
declare(strict_types=1);

use Civi\Lughauth\Features\Access\Master\Tenant\Domain\TenantRef;
use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Features\Access\Master\TenantTermsOfUse\Domain\TenantTermsOfUse;

final class TenantTermsOfUseUnitTest extends TestCase
{
    public function test_init_store_values(): void
    {
        // @Arrange
        $one = new TenantTermsOfUse(
            uid: 'one',
            tenant: new TenantRef('one'),
            text: 'one',
            enabled: true,
            attached: 'store://bin',
            activationDate: (new \DateTimeImmutable('1980-08-20T14:32:45.123Z')),
            version: 1,
        );

        // @Act
        $other = $one->toAttributes()->build();
        $calculated = TenantTermsOfUse::calculatedFields();

        // @Assert
        $this->assertEquals([ 'enabled'], $calculated);
        $this->assertEquals($one->uid(), $other->uid());
        $this->assertEquals($one->getTenant(), $other->getTenant());
        $this->assertTrue($one->isTenantChanged());
        $this->assertEquals($one->getText(), $other->getText());
        $this->assertTrue($one->isTextChanged());
        $this->assertEquals($one->getAttached(), $other->getAttached());
        $this->assertTrue($one->isAttachedChanged());
        $this->assertEquals($one->getActivationDate(), $other->getActivationDate());
        $this->assertTrue($one->isActivationDateChanged());
        $this->assertEquals($one->getVersion(), $other->getVersion());
        $this->assertTrue($one->isVersionChanged());
    }
    public function test_replace_change_values(): void
    {
        // @Arrange
        $base = new TenantTermsOfUse(
            uid: 'one',
            tenant: new TenantRef('one'),
            text: 'one',
            enabled: true,
            attached: 'store://bin',
            activationDate: (new \DateTimeImmutable('1980-08-20T14:32:45.123Z')),
            version: 1,
        );
        $other = new TenantTermsOfUse(
            uid: 'other',
            tenant: new TenantRef('other'),
            text: 'other',
            enabled: false,
            attached: 'store://obin',
            activationDate: (new \DateTimeImmutable('1981-09-06T14:32:45.123Z')),
            version: 2,
        );

        // @Act
        $one = $base->replace($other->toAttributes());

        // @Assert
        $this->assertEquals($one->uid(), $base->uid());
        $this->assertEquals($one->getTenant(), $other->getTenant());
        $this->assertTrue($one->isTenantChanged($base));
        $this->assertEquals($one->getText(), $other->getText());
        $this->assertTrue($one->isTextChanged($base));
        $this->assertEquals($one->getAttached(), $other->getAttached());
        $this->assertTrue($one->isAttachedChanged($base));
        $this->assertEquals($one->getActivationDate(), $other->getActivationDate());
        $this->assertTrue($one->isActivationDateChanged($base));
        $this->assertEquals($one->getVersion(), $other->getVersion());
        $this->assertTrue($one->isVersionChanged($base));
    }
    public function test_json_compare(): void
    {
        // @Arrange
        $one = new TenantTermsOfUse(
            uid: 'one',
            tenant: new TenantRef('one'),
            text: 'one',
            enabled: true,
            attached: 'store://bin',
            activationDate: (new \DateTimeImmutable('1980-08-20T14:32:45.123Z')),
            version: 1,
        );

        // @Act
        $json = $one->asPublicJson();

        // @Assert
        $this->assertEquals('one', $json['uid']);
        $this->assertEquals('one', $json['tenant']['$ref']);
        $this->assertEquals(true, $json['enabled']);
        $this->assertEquals((new \DateTimeImmutable('1980-08-20T14:32:45.123Z')), $json['activationDate']);
        $this->assertEquals(1, $json['version']);
    }
